import { React, useState, useRef, useEffect, useContext } from 'react'
import { Link } from 'react-router-dom'
import './Header.css';
import MenuIcon from '@material-ui/icons/Menu';
import ClearIcon from '@material-ui/icons/Clear';
import { FaSearch } from "react-icons/fa";
import { AuthContext } from "../../Context/AuthContext.js"
import { useHistory } from "react-router-dom";
import Navbar from "../Navbar/Navbar";
import io from 'socket.io-client';
import {
    Avatar,
    Badge,
    Drawer,
    Button,
    List,
    Skeleton,
    Collapse,
    Typography,
    Form, 
    Modal
} from 'antd';
import { BellOutlined } from '@ant-design/icons';
const { Panel } = Collapse;
const { Text } = Typography;

const count = 10;
const fakeDataUrl = `https://randomuser.me/api/?results=${count}&inc=name,gender,email,nat,picture&noinfo`;
function Header() {
    const [menutoggle, setMenutoggle] = useState(false)
    const history = useHistory();
    const [initLoading, setInitLoading] = useState(true);
    const [loading, setLoading] = useState(false);
    const [data, setData] = useState([]);
    const [list, setList] = useState([]);
    const [open, setOpen] = useState(false);
    const [countNotify, setCountNotify] = useState(0);
    const [notifyStaff, setNotifyStaff] = useState(false);
    const [notifyUser, setNotifyUser] = useState(false);
    const [socket, setSocket] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);

    // lay count  Notify


    useEffect(() => {
        const fetchData = async () => {
            try {
                if (user.user.isAdmin) {
                    const response = await fetch(`http://localhost:5000/api/notify/countSaft/${user.user._id}`); // Thay đổi URL tùy thuộc vào đường dẫn của API
                    if (!response.ok) {
                        throw new Error('Failed to fetch books');
                    }
                    const data = await response.json();
                    setCountNotify(data); // Cập nhật state allBooks với dữ liệu từ API
                }
                else {
                    const response = await fetch(`http://localhost:5000/api/notify/countUesr/${user.user._id}`); // Thay đổi URL tùy thuộc vào đường dẫn của API
                    if (!response.ok) {
                        throw new Error('Failed to fetch books');
                    }
                    const data = await response.json();
                    setCountNotify(data); // Cập nhật state allBooks với dữ liệu từ API
                } // Cập nhật state allBooks với dữ liệu từ API
            } catch (error) {
                console.error('Error fetching books:', error);
            }
        };

        fetchData(); // Gọi fetchData để lấy dữ liệu
        const newSocket = io('http://localhost:8888'); // Thay đổi URL phù hợp với máy chủ của bạn
        setSocket(newSocket);

        // Lắng nghe sự kiện từ máy chủ
        newSocket.on('dataChanged', () => {
            // Khi có sự thay đổi, gọi lại fetchData để cập nhật số lượng thông báo
            fetchData();
        });

        // Cleanup function để đóng kết nối khi component unmount
        return () => {
            newSocket.disconnect();
        };
    }, []);
    //  lấy thông tin Notify
    console.log(countNotify)
    useEffect(() => {
        const fetchData = async () => {
            try {
                if (user.user.isAdmin) {
                    const response = await fetch(`http://localhost:5000/api/notify/staff-notify/${user.user._id}`); // Thay đổi URL tùy thuộc vào đường dẫn của API
                    if (!response.ok) {
                        throw new Error('Failed to fetch books');
                    }
                    const data = await response.json();
                    setNotifyStaff(data); // Cập nhật state allBooks với dữ liệu từ API
                }
                else {
                    const response = await fetch(`http://localhost:5000/api/notify/user-notify/${user.user._id}`); // Thay đổi URL tùy thuộc vào đường dẫn của API
                    if (!response.ok) {
                        throw new Error('Failed to fetch books');
                    }
                    const data = await response.json();
                    setNotifyUser(data); // Cập nhật state allBooks với dữ liệu từ API
                }


            } catch (error) {
                console.error('Error fetching books:', error);
            }
        };

        fetchData(); // Gọi fetchData để lấy dữ liệu
        const newSocket = io('http://localhost:8888'); // Thay đổi URL phù hợp với máy chủ của bạn
        setSocket(newSocket);

        // Lắng nghe sự kiện từ máy chủ
        newSocket.on('dataChanged', () => {
            // Khi có sự thay đổi, gọi lại fetchData để cập nhật số lượng thông báo
            fetchData();
        });

        // Cleanup function để đóng kết nối khi component unmount
        return () => {
            newSocket.disconnect();
        };

    }, []);
    const handleNotifyClick = async (notify) => {
        try {
            // Gọi API để cập nhật NotifyStatus của thông báo
            await fetch(`http://localhost:5000/api/notify/update/${notify._id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ NotifyStatus: 0 }),
            });
            socket.emit('notifyChange');
            // Cập nhật lại danh sách thông báo trên giao diện
            if (user.user.isAdmin) {
                const response = await fetch(`http://localhost:5000/api/notify/countSaft/${user.user._id}`); // Thay đổi URL tùy thuộc vào đường dẫn của API
                if (!response.ok) {
                    throw new Error('Failed to fetch books');
                }
                const data = await response.json();
                setCountNotify(data); // Cập nhật state allBooks với dữ liệu từ API
            }
            else {
                const response = await fetch(`http://localhost:5000/api/notify/countUesr/${user.user._id}`); // Thay đổi URL tùy thuộc vào đường dẫn của API
                if (!response.ok) {
                    throw new Error('Failed to fetch books');
                }
                const data = await response.json();
                setCountNotify(data); // Cập nhật state allBooks với dữ liệu từ API
            } // Cập nhật state allBooks với dữ liệu từ API
        } catch (error) {
            console.error('Error updating notify:', error);
        }
    };

    console.log("dem", countNotify)
    // console.log("dem", notifyUser)

    const onChange = (key) => {
        console.log(key);
    };
    const showDrawer = () => {
        setOpen(true);
    };
    const onClose = () => {
        setOpen(false);
    };
    const Toggle = () => {
        setMenutoggle(!menutoggle)
    }

    const closeMenu = () => {
        setMenutoggle(false)
    }

    const { user } = useContext(AuthContext);

    const [searchTerm, setSearchTerm] = useState("");


    const handleSearch = (event) => {
        event.preventDefault();
        // Gửi yêu cầu tìm kiếm
        console.log("Từ khóa tìm kiếm:", searchTerm);

        history.push(`/search/${searchTerm}`);

    };

    const handleKeyPress = (event) => {
        if (event.key === "Enter") {
            handleSearch(event);
        }
    };
    // thong bao 
    useEffect(() => {
        fetch(fakeDataUrl)
            .then((res) => res.json())
            .then((res) => {
                setInitLoading(false);
                setData(res.results);
                setList(res.results);
            });
    }, []);
    const onLoadMore = () => {
        setLoading(true);
        setList(
            data.concat(
                [...new Array(count)].map(() => ({
                    loading: true,
                    name: {},
                    picture: {},
                })),
            ),
        );
        fetch(fakeDataUrl)
            .then((res) => res.json())
            .then((res) => {
                const newData = data.concat(res.results);
                setData(newData);
                setList(newData);
                setLoading(false);
                // Resetting window's offsetTop so as to display react-virtualized demo underfloor.
                // In real scene, you can using public method of react-virtualized:
                // https://stackoverflow.com/questions/46700726/how-to-use-public-method-updateposition-of-react-virtualized
                window.dispatchEvent(new Event('resize'));
            });
    };
    const loadMore =
        !initLoading && !loading ? (
            <div
                style={{
                    textAlign: 'center',
                    marginTop: 12,
                    height: 32,
                    lineHeight: '32px',
                }}
            >
                <Button onClick={onLoadMore}>loading more</Button>
            </div>
        ) : null;

    return (
        <div className="header">
            <div className="logo-nav">
                <Link to="/">
                    <a href="#home">THƯ VIỆN MTA </a>
                </Link>
            </div>
            <div className="nav-right">

                <div className="search-container">
                    <input className="search-input" type="text" placeholder="Tìm kiếm..."
                        value={searchTerm}
                        onChange={(event) => setSearchTerm(event.target.value)}
                        onKeyUp={handleKeyPress}

                    />

                </div>


                <ul className={menutoggle ? "nav-options active" : "nav-options"}>
                    <li className="option" onClick={() => { closeMenu() }}>
                        <Link to="/">
                            <a href="#home">Trang chủ</a>
                        </Link>
                    </li>
                    <li className="option" onClick={() => { closeMenu() }}>
                        <Link to="/books">
                            <a href="#books">Kho sách</a>
                        </Link>
                    </li>

                    {!user ? (
                        <>
                            <li className="option" onClick={() => { closeMenu() }}>
                                <Link to="/signin">
                                    <a href="signin">Đăng nhập</a>
                                </Link>
                            </li>

                            <li className="option" onClick={() => { closeMenu() }}>
                                <Link to="/signup">
                                    <a href="signup">Đăng ký</a>
                                </Link>
                            </li>


                        </>
                    ) : (
                        <>
                            <li className="option" onClick={() => { closeMenu() }}>
                                <Link to="/signin">
                                    <a href="signin">Admin</a>
                                </Link>
                            </li>
                            <li className="option" onClick={() => { closeMenu() }}>
                                <a href="#">
                                    <Badge count={countNotify}>
                                        {/* <Avatar shape="square" size="large" /> */}
                                        <div className="bell-icon">
                                            <BellOutlined
                                                style={{
                                                    fontSize: '25px',
                                                    color: '#fff',
                                                    padding: '4px'
                                                }}
                                                onClick={showDrawer}
                                            />
                                        </div>
                                    </Badge>
                                </a>
                            </li>
                        </>
                    )}

                </ul>
            </div>
            <div className="mobile-menu" onClick={() => { Toggle() }}>
                {menutoggle ? (
                    <ClearIcon className="menu-icon" style={{ fontSize: 40 }} />
                ) : (
                    <MenuIcon className="menu-icon" style={{ fontSize: 40 }} />
                )}
            </div>

            {/* <Drawer title="Thông báo" onClose={onClose} open={open}>
                <List
                    className="demo-loadmore-list"
                    loading={initLoading}
                    itemLayout="horizontal"
                    loadMore={loadMore}
                    // dataSource={notifyStaff} // Sử dụng user.isAdmin để xác định người dùng có phải là admin không

                    dataSource={user && user.user.isAdmin ? notifyStaff : notifyUser} // Sử dụng user.isAdmin để xác định người dùng có phải là admin không
                    renderItem={(item) => (
                        <List.Item>
                            <List.Item.Meta
                                title={<a onClick={() => handleNotifyClick(item)}>{item.NotifyName}</a>}
                                description={item.description} // Hiển thị mô tả của thông báo
                            />
                        </List.Item>
                    )}
                />
            </Drawer> */}

            <Drawer title="Thông báo" onClose={onClose} open={open}>
                <List
                    className="demo-loadmore-list"
                    loading={initLoading}
                    itemLayout="horizontal"
                    loadMore={loadMore}
                    dataSource={user && user.user.isAdmin ? notifyStaff : notifyUser}
                    renderItem={(item) => (
                        <List.Item className={item.NotifyStatus === 1 ? 'unread' : 'read'}>
                            <List.Item.Meta
                                title={
                                    <a
                                        onClick={() => {
                                            handleNotifyClick(item);
                                            item.NotifyStatus = 0; // Cập nhật trạng thái đã đọc
                                        }}
                                    >
                                        {item.NotifyName}
                                    </a>
                                }
                                description={item.description}
                            />
                        </List.Item>
                    )}
                />
            </Drawer>

            <Modal title="Mượn sách"
                open={isModalOpen}
                // onOk={handleOkModal}
                onCancel={handleCancel}
                width={1100}
                footer={[
                    <Button key="huy" onClick={handleCancel}>
                        Hủy
                    </Button>,
                    <Button key="them" type="primary" >
                        {/* onClick={handleOkModal}> */}
                        Thêm
                    </Button>,


                ]}
            >
                <Form
                    form={form}
                    // onFinish={onFinish} 
                    layout="vertical"
                    style={{ margin: 33, }}

                >
                    <Row gutter={16}>
                        <Col span={8}>
                            <Form.Item name="userType" label="User Type" rules={[{ required: true, message: 'Please select user type!' }]}>
                                <Select placeholder='User Type' fluid selection
                                // onChange={(value) => setUserType(value)}
                                >
                                    {/* Render options */}
                                </Select>
                            </Form.Item>

                            <Form.Item name="userFullName" label="Họ và tên" rules={[{ required: true, message: 'Please enter full name!' }]}>
                                <Input />
                            </Form.Item>

                            <Form.Item name="email" label="Email" rules={[{ required: true, message: 'Please enter email!', type: 'email' }]}>
                                <Input />
                            </Form.Item>

                            <Form.Item name="password" label="Mật khẩu" rules={[{ required: true, message: 'Please enter password!' }]}>
                                <Input.Password />
                            </Form.Item>
                        </Col>
                        <Col span={8}>
                            <Form.Item name="address" label="Địa chỉ" rules={[{ required: true, message: 'Please enter address!' }]}>
                                <Input />
                            </Form.Item>
                            <Form.Item name="mobileNumber" label="Số điện thoại" rules={[{ required: true, message: 'Please enter mobile number!' }]}>
                                <Input />
                            </Form.Item>

                            <Form.Item name="gender" label="Giới tính" rules={[{ required: true, message: 'Please select gender!' }]}>
                                <Select placeholder='User Type' fluid selection
                                // value={gender} onChange={(value) => setGender(value)}
                                >
                                    {/* Render options */}
                                </Select>
                            </Form.Item>

                            <Form.Item name="dob" label="Ngày sinh" rules={[{ required: true, message: 'Please select date of birth!' }]}>
                                <DatePicker placeholderText="dd/MM/YYYY" format="DD/MM/YYYY" />
                            </Form.Item>
                        </Col>
                        <Col span={8}>



                            <Form.Item
                                label="Ảnh đại diện"
                                name="photo"
                            >
                                <input type="file" onChange={handleChange} />
                                {imageURL && (
                                    <img
                                        style={{
                                            width: '200px',
                                            height: '200px'
                                        }}
                                        src={imageURL}
                                        alt="Ảnh bìa"
                                    />
                                )}
                            </Form.Item>
                        </Col>

                    </Row>

                    <Form.Item
                        style={{
                            paddingTop: 24,
                        }}
                    >
                        {/* <Button type="primary" htmlType="submit" loading={isLoading}>
                            XÁC NHẬN
                        </Button> */}
                    </Form.Item>
                </Form>
            </Modal>
        </div >




    )
}

export default Header
