import React, { useContext, useEffect, useState } from 'react';
import { Select, DatePicker, Table, Typography, Button, Form, Row, Col, message } from 'antd';
import axios from 'axios';
import { AuthContext } from '../../../../Context/AuthContext';
import moment from 'moment';
import Loading from '../../../../Components/Loader/Loader';
// import "./adtransaction.css"

const { Option } = Select;
const { Title } = Typography;

function AddTransaction() {
  const API_URL = process.env.REACT_APP_API_URL;
  const { user } = useContext(AuthContext);
  const [isLoading, setIsLoading] = useState(false);
  const [borrowerId, setBorrowerId] = useState('');
  const [borrowerDetails, setBorrowerDetails] = useState({});
  const [bookIds, setBookIds] = useState([]);
  const [bookId, setBookId] = useState("")
  const [transactionType, setTransactionType] = useState('');
  const [toDate, setToDate] = useState(null);
  const [allMembers, setAllMembers] = useState([]);
  const [allBooks, setAllBooks] = useState([]);
  const [recentTransactions, setRecentTransactions] = useState([]);
  const [allTransactions, setAllTransactions] = useState([]);
  const [fromDate, setFromDate] = useState(null)
  const [ExecutionStatus, setExecutionStatus] = useState(null) /* For triggering the tabledata to be updated */




  const transactionTypes = [
    { value: 'Reserved', text: 'ƒê·∫∑t tr∆∞·ªõc' },
    { value: 'Issued', text: 'M∆∞·ª£n lu√¥n' },
  ];
  useEffect(() => {
    const getAllTransactions = async () => {
      try {
        const response = await axios.get(API_URL + "api/transactions/all-transactions")
        setAllTransactions(response.data.sort((a, b) => Date.parse(a.toDate) - Date.parse(b.toDate)).filter((data) => {
          return data.transactionStatus === "Active"
        }))
        console.log("Okay")
        setExecutionStatus(null)
      }
      catch (err) {
        console.log(err)
      }
    }
    getAllTransactions()
  }, [API_URL, ExecutionStatus])
  // Fetching all members
  useEffect(() => {
    const getMembers = async () => {
      try {
        const response = await axios.get(API_URL + 'api/users/allmembers');
        const allMembers = response.data.map(member => ({
          value: member._id,
          text: `${member.userFullName} [${member.userType === 'Student' ? member.admissionId : member.employeeId}]`,
        }));
        setAllMembers(allMembers);
      } catch (error) {
        console.log('Error fetching members:', error);
      }
    };
    getMembers();
  }, [API_URL]);

  // Fetching all books
  useEffect(() => {
    const getBooks = async () => {
      try {
        const response = await axios.get(API_URL + 'api/books/allbooks');
        const allBooks = response.data.map(book => ({
          value: book._id,
          text: book.bookName,
        }));
        setAllBooks(allBooks);
      } catch (error) {
        console.log('Error fetching books:', error);
      }
    };
    getBooks();
  }, [API_URL]);

  const handleBookSelection = selectedBooks => {
    setBookIds(selectedBooks);
  };

  const addTransaction = async (e) => {

    e.preventDefault()
    setIsLoading(true)
    if (
      bookId !== '' &&
      borrowerId !== '' &&
      transactionType !== '' &&
      fromDate !== null &&
      toDate !== null
    ) {
      const borrower_details = await axios.get(
        API_URL + 'api/users/getuser/' + borrowerId,
      )
      const book_details = await axios.get(
        API_URL + 'api/books/getbook/' + bookId,
      )

      /* Checking weather the book is available or not */
      console.log(book_details)
      // issued l√† ƒë√£ m∆∞·ª£n, reserved l√† ƒë·ªôc gi·∫£ ƒë√£ ƒë·∫∑t tr∆∞·ªõc cu·ªën s√°ch
      if (
        (book_details.data.bookCountAvailable > 0 &&
          (transactionType === 'Issued' || transactionType === 'Reserved')) ||
        (book_details.data.bookCountAvailable === 0 &&
          transactionType === 'Reserved')
      ) {
        const transactionData = {
          bookId: bookId,
          borrowerId: borrowerId,
          borrowerName: borrower_details.data.userFullName,
          bookName: book_details.data.bookName,
          transactionType: transactionType,
          fromDate: fromDate,
          toDate: toDate,
          isAdmin: user.isAdmin,
        }
        try {
          const response = await axios.post(
            API_URL + 'api/transactions/add-transaction',
            transactionData,
          )
          if (recentTransactions.length >= 5) {
            recentTransactions.splice(-1)
          }
          await axios.put(
            API_URL +
            `api/users/${response.data._id}/move-to-activetransactions`,
            {
              userId: borrowerId,
              isAdmin: user.isAdmin,
            },
          )

          await axios.put(API_URL + 'api/books/updatebook/' + bookId, {
            isAdmin: user.isAdmin,
            bookCountAvailable: book_details.data.bookCountAvailable - 1,
          })

          setRecentTransactions([response.data, ...recentTransactions])
          setBorrowerId('')
          setBookId([])
          setTransactionType('')
          setFromDate(null)
          setToDate(null)
          setFromDateString(null)
          setToDateString(null)
          alert('T·∫°o phi·∫øu m∆∞·ª£n th√†nh c√¥ng üéâ')
        } catch (err) {
          console.log(err)
        }
      } else {
        alert('Cu·ªën s√°ch n√†y ƒë√£ h·∫øt')
      }
    } else {
      alert('Kh√¥ng ƒë∆∞·ª£c b·ªè tr·ªëng c√°c tr∆∞·ªùng')
    }
    setIsLoading(false)
  }

  const convertToIssue = async (transactionId) => {
    try {
      await axios.put(API_URL + "api/transactions/update-transaction/" + transactionId, {
        transactionType: "Issued",
        isAdmin: user.isAdmin
      })
      setExecutionStatus("Completed");
      alert("Chuy·ªÉn tr·∫°ng th√°i 'ƒê√£ m∆∞·ª£n' th√†nh c√¥ng üéÜ")
    }
    catch (err) {
      console.log(err)
    }
  }

  if (isLoading) return <Loading />;

  return (
    <div>
      <Title level={2}>T·∫°o phi·∫øu m∆∞·ª£n s√°ch</Title>
      {/* Form for adding transaction */}
      <Form
        onFinish={addTransaction}
        layout="vertical"
        style={{ margin: 33, }}
      >
        <Row gutter={16}>
          <Col span={12}>
            <Form.Item
              label="ƒê·ªôc gi·∫£"
              name="borrowerId"
              rules={[{ required: true, message: 'Vui l√≤ng ch·ªçn ƒë·ªôc gi·∫£' }]}
              labelStyle={{ fontSize: '20px' }} // Th√™m style cho nh√£n
            >
              <Select
                placeholder="Ch·ªçn ƒë·ªôc gi·∫£"
                onChange={setBorrowerId}
                style={{ fontSize: '16px' }} // Th√™m style cho Select component
              >
                {allMembers.map(member => (
                  <Option key={member.value} value={member.value}>{member.text}</Option>
