import React, { useEffect, useState } from 'react';
import './BookList.css';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { List } from 'antd';
import Loading from '../Loader/Loader';
import Footer from '../Footer/Footer';
import CateController from '../../Controller/CateController';

function ListCate() {
    const API_URL = process.env.REACT_APP_API_URL;

    const [listCate, setListCate] = useState([]);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        setLoading(true);
        const getListCate = async () => {
            try {
                const response = await axios.get(API_URL + "api/books/getallcate");
                setListCate(response.data);
            } catch (err) {
                console.log('Error when get list book =>', err);
            } finally {
                setLoading(false);
            }
        };
        getListCate();
    }, []);

    if (loading) return <Loading />;

    return (
        <div className="block-page">
            <div className="books-page">
                <div className="title">
                    <label htmlFor="category">Danh sách thể loại</label><br />
                </div>

                <div className="books">
                    <List
                        dataSource={listCate}
                        renderItem={item => (
                            <List.Item>
                                <Link to={`/category/${item}`}>
                                    <p className="bookcard-title"><b>{item}</b></p>
                                </Link>
                            </List.Item>
                        )}
                    />
                </div>
            </div>
            <Footer />
        </div>
    );
}

export default ListCate;
