import React, { useContext, useEffect, useState } from 'react';
import { Select, DatePicker, Table, Typography, Button, Form, Row, Col, message } from 'antd';
import axios from 'axios';
import { AuthContext } from '../../../../Context/AuthContext';
import moment from 'moment';
import Loading from '../../../../Components/Loader/Loader';
import TransController from '../../../../Controller/TransactionController';
import UserController from '../../../../Controller/UserController';
// import "./adtransaction.css"

const { Option } = Select;
const { Title } = Typography;

function AddTransaction() {
  const API_URL = process.env.REACT_APP_API_URL;
  const { user } = useContext(AuthContext);
  const [isLoading, setIsLoading] = useState(false);
  const [borrowerId, setBorrowerId] = useState('');
  const [borrowerDetails, setBorrowerDetails] = useState({});
  const [bookIds, setBookIds] = useState([]);
  const [bookId, setBookId] = useState("")
  const [transactionType, setTransactionType] = useState('');
  const [toDate, setToDate] = useState(null);
  const [allMembers, setAllMembers] = useState([]);
  const [allBooks, setAllBooks] = useState([]);
  const [recentTransactions, setRecentTransactions] = useState([]);
  const [allTransactions, setAllTransactions] = useState([]);
  const [fromDate, setFromDate] = useState(null)
  const [ExecutionStatus, setExecutionStatus] = useState(null) /* For triggering the tabledata to be updated */




  const transactionTypes = [
    { value: 'Reserved', text: 'Đặt trước' },
    { value: 'Issued', text: 'Mượn luôn' },
  ];
  // lay danh sach transaction chua xu ly
  useEffect(() => {
    const fetchData = async () => {
      try {
        const allTransactions = await TransController.getAllTrans();
        // Lọc danh sách các giao dịch có trạng thái là "Active"
        const activeTransactions = allTransactions.filter(transaction => transaction.transactionStatus === "Active");
        setAllTransactions(activeTransactions); // Đặt danh sách các giao dịch "Active" vào state
      } catch (error) {
        console.error('Error fetching transactions:', error);
      }
    };

    fetchData(); // Gọi fetchData để lấy dữ liệu

  }, []); // useEffect sẽ chỉ chạy một lần sau khi render lần đầu tiên

  // lay danh sach nguoi dung khong phai la admin
  useEffect(() => {
    const fetchData = async () => {
      try {
        const allUser = await UserController.getAllUsers();
        // Lọc danh sách các giao dịch có trạng thái là "Active"
        const activeTransactions = allTransactions.filter(transaction => transaction.transactionStatus === "Active");
        setAllTransactions(activeTransactions); // Đặt danh sách các giao dịch "Active" vào state
      } catch (error) {
        console.error('Error fetching transactions:', error);
      }
    };

    fetchData(); // Gọi fetchData để lấy dữ liệu

  }, []);
  useEffect(() => {
    const getMembers = async () => {
      try {
        const response = await axios.get(API_URL + 'api/users/allmembers');
        const allMembers = response.data.map(member => ({
          value: member._id,
          text: `${member.userFullName} [${member.userType === 'Student' ? member.admissionId : member.employeeId}]`,
        }));
        setAllMembers(allMembers);
      } catch (error) {
        console.log('Error fetching members:', error);
      }
    };
    getMembers();
  }, [API_URL]);

  // Fetching all books
  useEffect(() => {
    const getBooks = async () => {
      try {
        const response = await axios.get(API_URL + 'api/books/allbooks');
        const allBooks = response.data.map(book => ({
          value: book._id,
          text: book.bookName,
        }));
        setAllBooks(allBooks);
      } catch (error) {
        console.log('Error fetching books:', error);
      }
    };
    getBooks();
  }, [API_URL]);

  const handleBookSelection = selectedBooks => {
    setBookIds(selectedBooks);
  };
  const handleUer = selectedBooks => {
    setBookIds(selectedBooks);
  };

  const addTransaction = async (values) => {
    try {
      setIsLoading(true);

      // Extracting form values
      const { borrowerId, bookIds, transactionType, fromDate, toDate } = values;

      // Checking if all required fields are filled
      if (
        borrowerId &&
        bookIds.length > 0 &&
        transactionType &&
        fromDate &&
        toDate
      ) {
        const borrower_details = await axios.get(API_URL + 'api/users/getuser/' + borrowerId);

        // Loop through selected bookIds and get details for each book
        const bookDetailsPromises = bookIds.map(bookId =>
          axios.get(API_URL + 'api/books/getbook/' + bookId)
        );
        const book_details = await Promise.all(bookDetailsPromises);

        // Checking book availability and transaction type
        const availableBooks = book_details.filter(book => book.data.bookCountAvailable > 0);
        const reservedBooks = book_details.filter(book => book.data.bookCountAvailable === 0);

        if (
          (availableBooks.length > 0 && (transactionType === 'Issued' || transactionType === 'Reserved')) ||
          (reservedBooks.length > 0 && transactionType === 'Reserved')
        ) {
          // Constructing transaction data
          const transactionData = {
            bookIds: bookIds,
            borrowerId: borrowerId,
            borrowerName: borrower_details.data.userFullName,
            books: book_details.map(book => book.data),
            transactionType: transactionType,
            fromDate: fromDate,
            toDate: toDate,
            isAdmin: user.isAdmin,
          };

          // Adding transaction
          const response = await axios.post(API_URL + 'api/transactions/add-transaction', transactionData);

          // Moving borrower to active transactions


          // Alerting success message
          alert('Tạo phiếu mượn thành công 🎉');
        } else {
          alert('Cuốn sách này đã hết hoặc không phù hợp loại giao dịch');
        }
      } else {
        alert('Vui lòng điền đầy đủ thông tin');
      }
    } catch (err) {
      console.log(err);
      alert('Đã xảy ra lỗi khi tạo phiếu mượn');
    } finally {
      setIsLoading(false);
    }
  };
  const convertToIssue = async (transactionId) => {
    try {
      await axios.put(API_URL + "api/transactions/update-transaction/" + transactionId, {
        transactionType: "Issued",
        isAdmin: user.isAdmin
      })
      setExecutionStatus("Completed");
      alert("Chuyển trạng thái 'Đã mượn' thành công 🎆")
    }
    catch (err) {
      console.log(err)
    }
  }

  if (isLoading) return <Loading />;

  return (
    <div>
      <Title level={2}>Tạo phiếu mượn sách</Title>
      {/* Form for adding transaction */}
      <Form
        // form={form}
        onFinish={addTransaction}
        layout="vertical"
        style={{ margin: 33, }}
      >
        <Row gutter={16}>
          <Col span={12}>
            <Form.Item
              label="Độc giả"
              name="borrowerId"
              rules={[{ required: true, message: 'Vui lòng chọn độc giả' }]}
              labelStyle={{ fontSize: '20px' }} // Thêm style cho nhãn
            >
              <Select
                placeholder="Chọn độc giả"
                onChange={setBorrowerId}
                style={{ fontSize: '16px' }} // Thêm style cho Select component
              >
                {allMembers.map(member => (
                  <Option key={member.value} value={member.value}>{member.text}</Option>
                ))}
              </Select>


            </Form.Item>
            <Form.Item label="Tên sách" name="bookIds" rules={[{ required: true, message: 'Vui lòng chọn sách' }]}>
              <Select mode="multiple" placeholder="Chọn sách" onChange={handleBookSelection}>
                {allBooks.map(book => (
                  <Option key={book.value} value={book.value}>{book.text}</Option>
                ))}
              </Select>
            </Form.Item>
            <Form.Item label="Loại mượn" name="transactionType" rules={[{ required: true, message: 'Vui lòng chọn loại mượn' }]}>
              <Select placeholder="Chọn loại mượn" onChange={setTransactionType}>
                {transactionTypes.map(type => (
                  <Option key={type.value} value={type.value}>{type.text}</Option>
                ))}
              </Select>
            </Form.Item>
          </Col>
          <Col span={12}>

            <Form.Item label="Từ ngày" name="fromDate" rules={[{ required: true, message: 'Vui lòng chọn từ ngày' }]}>
              <DatePicker style={{ width: 640 }} placeholder="Chọn từ ngày" onChange={date => setFromDate(date)} />
            </Form.Item>
            <Form.Item label="Đến ngày" name="toDate" rules={[{ required: true, message: 'Vui lòng chọn đến ngày' }]}>
              <DatePicker style={{ width: 640 }} placeholder="Chọn đến ngày" onChange={date => setToDate(date)} />
            </Form.Item>
          </Col>

        </Row>

        <Form.Item
          style={{
            paddingTop: 24,
          }}
        >
          <Button type="primary" htmlType="submit" loading={isLoading}>Submit</Button>
        </Form.Item>
      </Form>

      {/* Table showing recent transactions */}

      <p className="dashboard-option-title">Chờ xử lý</p>
      <table className="admindashboard-table">
        <tr>
          <th>Tên sách</th>
          <th>Người mượn</th>
          <th>Từ ngày</th>
          <th>Đến ngày</th>
          <th></th>
        </tr>
        {
          Array.isArray(allTransactions) && allTransactions.length > 0 ?
            allTransactions.map((data, index) => {
              console.log("kiet => ", data);
              return (
                <tr key={index}>
                  <td>{data.books}</td>
                  <td>{data.userName}</td>
                  <td>{data.fromDate}</td>
                  <td>{data.toDate}</td>
                  <td><button onClick={() => { convertToIssue(data._id) }}> Cho mượn</button></td>
                </tr>
              )
            }) : (
              <tr>
                <td colSpan="5">Không có giao dịch nào được tìm thấy</td>
              </tr>
            )
        }
      </table>
    </div>

  );
}

export default AddTransaction;
