import axios from "axios"
const API_URL = process.env.REACT_APP_API_URL;
const TransApi = {
  getAllTrans: async () => {
    const res = await axios.get("http://localhost:5000/api/transactions/all-transactions")
    return res.data
  },
  getTransbyID: async (transactionId) => {
    const res = await axios.get(API_URL + `api/transactions/${transactionId}`)
    return res.data
  },
  getTransbyUser: async (userId) => {
    const res = await axios.get(API_URL + `api//transactions/user-transactions/${userId}`)
    return res.data
  },
  addTrans: async (data) => {
    const res = await axios.post(API_URL + "api/transactions/add-transaction", data)
    return res.data
  },
  updateTrans: async (transactionId, data) => {
    const res = await axios.put(`http://localhost:5000/api/transactions/update-transactionStatus/${transactionId}`, data);

    // const res = await axios.put(API_URL + `api/transactions/update-transactionStatus/${transactionId}`, data)
    return res.data
  },
  deleteTrans: async (transactionId) => {
    const res = await axios.delete(API_URL + `api/transactions/remove-transaction/${transactionId}`)
    return res.data
  },
}

export default TransApi
