import React, { useState } from 'react';
import { Menu, Layout } from 'antd';
import { UserOutlined, BookOutlined, ReadOutlined, TeamOutlined, UserAddOutlined, LogoutOutlined } from '@ant-design/icons';

const { Sider } = Layout;
const { SubMenu } = Menu;

function MemberDashboard() {
  const [active, setActive] = useState("profile");
  const [collapsed, setCollapsed] = useState(false);

  const handleClick = (e) => {
    setActive(e.key);
  };

  const handleCollapse = () => {
    setCollapsed(!collapsed);
  };

  const logout = () => {
    localStorage.removeItem("user");
    // Redirect to SignIn page
    window.location.href = '/signin';
  };

  return (
    <Layout style={{ minHeight: '100vh' }}>
      <Sider
        collapsible
        collapsed={collapsed}
        onCollapse={handleCollapse}
        width={300} // Adjust sidebar width here
      >
        <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline" onClick={handleClick} selectedKeys={[active]}>
          <Menu.Item key="profile" icon={<UserOutlined />} style={{ fontSize: '16px', fontWeight: 'bold' }}>
            Thông tin cá nhân
          </Menu.Item>
          <Menu.Item key="returntransaction" icon={<ReadOutlined />} style={{ fontSize: '16px', fontWeight: 'bold' }}>
            Mượn trả sách
          </Menu.Item>
          <Menu.Item key="logout" icon={<LogoutOutlined />} onClick={logout} style={{ fontSize: '16px', fontWeight: 'bold' }}>
            Đăng xuất
          </Menu.Item>
        </Menu>
      </Sider>
    </Layout>
  );
}

export default MemberDashboard;
