import React, { useEffect, useState } from 'react'
import './BookList.css'
import axios from 'axios'
import Loading from "../Loader/Loader";
import { Link } from 'react-router-dom';
import { useParams } from 'react-router-dom';
import { FaArrowLeft } from "react-icons/fa";
import { useHistory } from "react-router-dom"
import Footer from '../Footer/Footer'
import {
    Button, Modal,
    Cascader,
    DatePicker,
    Form,
    Input,
    InputNumber,
    Mentions,
    Select,
    TreeSelect,
} from 'antd';
import getUrlImage from "../../Pages/getURLimage"
import TransController from '../../Controller/TransactionController';
import { AuthContext } from '../../Context/AuthContext.js';
import Breadcrumb from '../Breadcrumb/Breadcrumb';

function BookList() {
    const [fromDate, setFromDate] = useState(null)
    const [toDate, setToDate] = useState(null);
    const [book, setBook] = useState(null);
    const { category } = useParams();
    const [listBook, setListbook] = useState([])
    const [loading, setLoading] = useState(false);
    const history = useHistory();
    const [isModalOpen, setIsModalOpen] = useState(false);
    const { user } = useContext(AuthContext)

    const handleOk = () => {
        setIsModalOpen(false);
    };
    const handleCancel = () => {
        setIsModalOpen(false);
    };

    const API_URL = process.env.REACT_APP_API_URL

    useEffect(() => {
        setLoading(true)
        const getListBook = async () => {
            try {
                if (category !== "") {
                    const response = await axios.get(API_URL + 'api/books/getbycate/' + category)
                    setListbook(response.data);

                } else {
                    const response = await axios.get(API_URL + 'api/books/allbooks')
                    setListbook(response.data);
                }



                setLoading(false);

            } catch (err) {
                setLoading(false);
                console.log('Error when get list book =>', err)
            }
        }
        getListBook()
    }, [API_URL])
    const handleBorrowClick = (book) => {
        setBook(book);
        setIsModalOpen(true);
    };
    // add transactions
    const addTransaction = async (values) => {
        try {
            setLoading(true);

            // Extracting form values
            const { borrowerId, bookIds, transactionType, fromDate, toDate } = values;


            const transactionData = {
                books: bookIds,
                borrowerId: borrowerId,
                transactionType: transactionType,
                fromDate: fromDate,
                toDate: toDate,
                returnDate: null
                // isAdmin: user.isAdmin,
            };

            // Adding transaction
            await TransController.addTransaction(transactionData);

            // Alerting success message
            alert('Tạo phiếu mượn thành công 🎉');
            //   } else {
            //     alert('Cuốn sách này đã hết hoặc không phù hợp loại giao dịch');
            //   }
            // } else {
            //   alert('Vui lòng điền đầy đủ thông tin');
            // }
        } catch (err) {
            console.log(err);
            alert('Đã xảy ra lỗi khi tạo phiếu mượn');
        } finally {
            setLoading(false);
        }
    };
    const handleUpdateTransaction = async (transactionId) => {
        try {
            const data = {
                transactionStatus: "True"
            };
            // Gọi hàm updateTransaction từ TransController để cập nhật giao dịch
            const updatedTransaction = await TransController.updateTransaction(transactionId, data);
            const Transactions = await TransController.getAllTrans();

            // Lọc danh sách các giao dịch có trạng thái là "Active"
            // const activeTransactions = allTransactions.filter(transaction => transaction && transaction.transactionStatus === "Active");

            console.log('Transaction updated:', updatedTransaction);
            alert('cập nhật thành công 🎉');
            const activeTransactions = Transactions.filter(transaction => transaction && transaction.transactionStatus && transaction.transactionStatus === "Active");
            console.log("thanh", Transactions)

        } catch (error) {
            console.error('Error updating transaction:', error);
        }
    };

    const handleOkModal = () => {
        // Thu thập thông tin từ các trường nhập liệu trong modal
        const formData = {
            borrowerId: 'ID_nguoi_muon', // Thay bằng thông tin thực tế
            bookIds: [book._id], // Sử dụng _id của sách đã chọn
            transactionType: 'Muon', // Có thể cần điều chỉnh tùy theo yêu cầu của hệ thống
            fromDate: fromDate,
            toDate: toDate
        };

        // Gọi hàm thêm giao dịch mới
        addTransaction(formData);
        // Đóng modal sau khi thêm giao dịch
        setIsModalOpen(false);
    };
    if (loading) return <Loading />;

    return (
        <div className="block-page">

            <div className="books-page">
                <button type='button' className='addbook-submit' onClick={() => history.push("/books")}>
                    <FaArrowLeft size={22} />
                    <span className='fs-18 fw-6'>Trở lại</span>
                </button>
                {/* <Breadcrumb /> */}
                <div className="title">
                    <label htmlFor="language">Sách theo thể loại "{category}"</label><br />
                </div>
                <div className="books">


                    {listBook.map((item) => {
                        return (

                            <div className="book-card">
                                <img src={getUrlImage(item.image_url)} alt="" href={`/book/${item._id}`} ></img>
                                <Link to={`/book/${item._id}`}>

                                    <p className="bookcard-title"><b>{item.bookName}</b></p>

                                </Link>
                                <p className="bookcard-author"> {item.author}</p>
                                <p className="bookcard-author">{item.publisher}</p>

                                <div>
                                    <div className="bookcard-category">
                                        <p>{item.categories}</p>
                                    </div>
                                    <div className="bookcard-select">
                                        <Button onClick={() => handleBorrowClick(item)}>Mượn</Button>

                                    </div>
                                </div>
                                <div className="bookcard-emptybox"></div>
                            </div>
                        )
                    })}
                </div>
            </div>
            <Modal title="Mượn sách" open={isModalOpen} onOk={handleOk} onCancel={handleCancel}>
                <Form
                    // form={form}

                    layout="vertical"
                    style={{ margin: 33, }}
                >
                    <Form.Item label="Mượn sách" name="book" rules={[{ required: true, message: 'Vui lòng chọn từ ngày' }]}>
                        <Input style={{ width: 400 }} placeholder={book ? book.bookName : ''} value={book ? book._id : ''} readOnly />
                    </Form.Item>
                    <Form.Item label="Từ ngày" name="fromDate" rules={[{ required: true, message: 'Vui lòng chọn từ ngày' }]}>
                        <DatePicker style={{ width: 400 }} placeholder="Chọn từ ngày" onChange={date => setFromDate(date)} />
                    </Form.Item>
                    <Form.Item label="Đến ngày" name="toDate" rules={[{ required: true, message: 'Vui lòng chọn đến ngày' }]}>
                        <DatePicker style={{ width: 400 }} placeholder="Chọn đến ngày" onChange={date => setToDate(date)} />
                    </Form.Item>

                </Form>
            </Modal>

            <Footer />
        </div>
    )
}

export default BookList
