import React, { useEffect, useContext, useState } from 'react';

// import "../AdminDashboard.css";
import axios from 'axios';
import { AuthContext } from '../../../../Context/AuthContext';
import { Dropdown } from 'semantic-ui-react';
import { UploadOutlined } from '@ant-design/icons';

import {
    ref,
    uploadBytes,
    getDownloadURL,
    listAll
} from "firebase/storage";
import { storage } from "../../../../firebase";
import { Select, DatePicker, Table, Typography, Button, Form, Row, Col, message, Upload, Input } from 'antd';
const { Option } = Select;
const { Title } = Typography;
function AddBook() {
    const API_URL = process.env.REACT_APP_API_URL
    const { user } = useContext(AuthContext);

    const [recentAddedBooks, setRecentAddedBooks] = useState([]);
    const [bookName, setBookName] = useState("");
    const [author, setAuthor] = useState("");
    const [bookCountAvailable, setBookCountAvailable] = useState(null);
    const [language, setLanguage] = useState("vi");
    const [publisher, setPublisher] = useState("");
    const [allCategories, setAllCatagories] = useState([]);
    const [selectedCategories, setSelectedCategories] = useState("");
    const [isLoading, setIsLoading] = useState(false);
    const [alternateTitle, setAlternateTitle] = useState("");
    const [imageUpload, setImageUpload] = useState(null);
    const [imageURL, setImageURL] = useState("");

    const [openModal, setOpenModal] = useState(false);

    const [categoryName, setCategoryName] = useState('');

    const [selectedImage, setSelectedImage] = useState(null);
    // eslint-disable-next-line no-unused-expressions
    // useEffect(() => {
    //     const getAllCategories = async () => {
    //         try {
    //             const response = await axios.get(API_URL + "api/books/getallcate")
    //             const all_categories = await response.data.map((category) => (
    //                 { value: `${category}`, text: `${category}` }
    //             ))
    //             setAllCatagories(all_categories)
    //         }
    //         catch (err) {
    //             console.log(err)
    //         }
    //     }
    //     getAllCategories()
    // }, [API_URL])
    const [form] = Form.useForm();
    const onFinish = (values) => {
        setIsLoading(true);
        // G·ª≠i d·ªØ li·ªáu ƒëi
        console.log('Received values:', values);
        setIsLoading(false);
    };



    useEffect(() => {
        console.log(imageUpload);

    }, [imageUpload])

    const handleImageUpload = (info) => {
        const { file } = info;

        const reader = new FileReader();
        reader.onload = (e) => {
            setSelectedImage(e.target.result);
        };

        if (file) {
            reader.readAsDataURL(file);
        }
    };

    const addCate = async (e) => {
        e.preventDefault();

        const CateData = {
            categoryName: categoryName
        }

        try {
            const response = await axios.post(API_URL + "api/categories/addcategory", CateData)

            alert("Th√™m th·ªÉ lo·∫°i '" + categoryName + "' th√†nh c√¥ng üéâ");
            setOpenModal(false);
            setCategoryName("");
        } catch (error) {
            console.log(error);
        }
    }

    const addBook = async (e) => {
        e.preventDefault();
        setIsLoading(true);

        if (imageUpload == null) return;
        const imageRef = ref(storage, `image/${imageUpload.name}`);
        uploadBytes(imageRef, imageUpload).then((snapshot) => {
            getDownloadURL(snapshot.ref).then((url) => {
                setImageURL(url);
            });
        });


        const BookData = {
            bookName: bookName,
            alternateTitle: alternateTitle,
            author: author,
            bookCountAvailable: bookCountAvailable,
            language: language,
            publisher: publisher,
            categories: selectedCategories,
            isAdmin: user.isAdmin,
            image_url: imageUpload.name
        }

        try {
            const response = await axios.post(API_URL + "api/books/addbook", BookData)
            if (recentAddedBooks.length >= 5) {
                recentAddedBooks.splice(-1)
            }
            setRecentAddedBooks([response.data, ...recentAddedBooks])
            setBookName("")
            setAlternateTitle("")
            setAuthor("")
            setBookCountAvailable(null)
            setLanguage("")
            setPublisher("")
            setSelectedCategories([])
            setImageURL("");

            alert("Th√™m s√°ch th√†nh c√¥ng üéâ")
        } catch (error) {
            console.log(error);
        }
        setIsLoading(false);

    }

    useEffect(() => {
        const getAllBookks = async () => {
            const response = await axios.get(API_URL + "api/books/allbooks");
            setRecentAddedBooks(response.data.slice(0, 5));
        }

        getAllBookks();
    }, [API_URL]);


    return (
        <div>
            <p className="dashboard-option-title">Th√™m m·ªôt ƒë·∫ßu s√°ch m·ªõi</p>
            <div className="dashboard-title-line"></div>

            <Form
                form={form}
                name="addbook-form"
                onFinish={onFinish}
                // labelCol={{ span: 8 }}
                // wrapperCol={{ span: 16 }}
                // onFinish={addTransaction}
                layout="vertical"
                style={{ margin: 33, }}
            >
                <Row gutter={16}>
                    <Col span={8}>
                        <Form.Item
                            label="T√™n s√°ch"
                            name="bookName"
                            rules={[{ required: true, message: 'Vui l√≤ng nh·∫≠p t√™n s√°ch!' }]}
                        >
                            <Input />
                        </Form.Item>



                        <Form.Item
                            label="T√™n t√°c gi·∫£"
                            name="author"
                            rules={[{ required: true, message: 'Vui l√≤ng nh·∫≠p t√™n t√°c gi·∫£!' }]}
                        >
                            <Input />
                        </Form.Item>
                        <Form.Item label="Ng√¥n ng·ªØ" name="language">
                            <Input />
                        </Form.Item>
                        <Form.Item label="Nh√† xu·∫•t b·∫£n" name="publisher">
                            <Input />
                        </Form.Item>

                    </Col>
                    <Col span={8}>

                        <Form.Item
                            label="S·ªë l∆∞·ª£ng s√°ch s·∫µn c√≥"
                            name="copies"
                            rules={[{ required: true, message: 'Vui l√≤ng nh·∫≠p s·ªë l∆∞·ª£ng s√°ch s·∫µn c√≥!' }]}
                        >
                            <Input type="number" />
                        </Form.Item>

                        <Form.Item
                            label="Th·ªÉ lo·∫°i"
                            name="categories"
                            rules={[{ required: true, message: 'Vui l√≤ng ch·ªçn th·ªÉ lo·∫°i s√°ch!' }]}
                        >
                            <Select mode="multiple" placeholder="Ch·ªçn th·ªÉ lo·∫°i">
                                {/* Render options */}
                            </Select>
                        </Form.Item>


                        <Form.Item label="Ti√™u ƒë·ªÅ thay th·∫ø" name="alternateTitle">
                            <Input.TextArea rows={4} />
                        </Form.Item>



                    </Col>
                    <Col span={8}>

                        {/* <Form.Item
                            label="·∫¢nh b√¨a"
                            name="coverImage"
                            rules={[{ required: true, message: 'Vui l√≤ng t·∫£i l√™n ·∫£nh b√¨a s√°ch!' }]}
                        >
                            <Upload
                                beforeUpload={false}
                                listType="picture"
                                maxCount={1}
                                customRequest={handleImageUpload}
                            >
                                <Button icon={<UploadOutlined />}>T·∫£i ·∫£nh l√™n</Button>
                            </Upload>
                        </Form.Item> */}
                        <Form.Item
                            label="·∫¢nh b√¨a"
                            name="coverImage"
                            rules={[{ required: true, message: 'Vui l√≤ng t·∫£i l√™n ·∫£nh b√¨a s√°ch!' }]}
                        >
                            <Upload
                                beforeUpload={false}
                                listType="picture"
                                maxCount={1}
                                customRequest={handleImageUpload}
