import React, { useEffect, useContext, useState } from 'react';

import "../AdminDashboard.css";
import axios from 'axios';
import { AuthContext } from '../../../../Context/AuthContext';
import { Dropdown } from 'semantic-ui-react';

import {
    ref,
    uploadBytes,
    getDownloadURL,
    listAll
} from "firebase/storage";
import { storage } from "../../../../firebase";
import { Select, DatePicker, Table, Typography, Button, Form, Row, Col, message } from 'antd';
const { Option } = Select;
const { Title } = Typography;
function AddBook() {
    const API_URL = process.env.REACT_APP_API_URL
    const { user } = useContext(AuthContext);

    const [recentAddedBooks, setRecentAddedBooks] = useState([]);
    const [bookName, setBookName] = useState("");
    const [author, setAuthor] = useState("");
    const [bookCountAvailable, setBookCountAvailable] = useState(null);
    const [language, setLanguage] = useState("vi");
    const [publisher, setPublisher] = useState("");
    const [allCategories, setAllCatagories] = useState([]);
    const [selectedCategories, setSelectedCategories] = useState("");
    const [isLoading, setIsLoading] = useState(false);
    const [alternateTitle, setAlternateTitle] = useState("");
    const [imageUpload, setImageUpload] = useState(null);
    const [imageURL, setImageURL] = useState("");

    const [openModal, setOpenModal] = useState(false);

    const [categoryName, setCategoryName] = useState('');

    const [selectedImage, setSelectedImage] = useState(null);
    // eslint-disable-next-line no-unused-expressions
    // useEffect(() => {
    //     const getAllCategories = async () => {
    //         try {
    //             const response = await axios.get(API_URL + "api/books/getallcate")
    //             const all_categories = await response.data.map((category) => (
    //                 { value: `${category}`, text: `${category}` }
    //             ))
    //             setAllCatagories(all_categories)
    //         }
    //         catch (err) {
    //             console.log(err)
    //         }
    //     }
    //     getAllCategories()
    // }, [API_URL])

    useEffect(() => {
        console.log(imageUpload);

    }, [imageUpload])

    const handleImageUpload = (event) => {
        const file = event.target.files[0];
        const reader = new FileReader();

        reader.onload = (e) => {
            setSelectedImage(e.target.result);
        };

        if (file) {
            reader.readAsDataURL(file);
        }
    };

    const addCate = async (e) => {
        e.preventDefault();

        const CateData = {
            categoryName: categoryName
        }

        try {
            const response = await axios.post(API_URL + "api/categories/addcategory", CateData)

            alert("Thêm thể loại '" + categoryName + "' thành công 🎉");
            setOpenModal(false);
            setCategoryName("");
        } catch (error) {
            console.log(error);
        }
    }

    const addBook = async (e) => {
        e.preventDefault();
        setIsLoading(true);

        if (imageUpload == null) return;
        const imageRef = ref(storage, `image/${imageUpload.name}`);
        uploadBytes(imageRef, imageUpload).then((snapshot) => {
            getDownloadURL(snapshot.ref).then((url) => {
                setImageURL(url);
            });
        });


        const BookData = {
            bookName: bookName,
            alternateTitle: alternateTitle,
            author: author,
            bookCountAvailable: bookCountAvailable,
            language: language,
            publisher: publisher,
            categories: selectedCategories,
            isAdmin: user.isAdmin,
            image_url: imageUpload.name
        }

        try {
            const response = await axios.post(API_URL + "api/books/addbook", BookData)
            if (recentAddedBooks.length >= 5) {
                recentAddedBooks.splice(-1)
            }
            setRecentAddedBooks([response.data, ...recentAddedBooks])
            setBookName("")
            setAlternateTitle("")
            setAuthor("")
            setBookCountAvailable(null)
            setLanguage("")
            setPublisher("")
            setSelectedCategories([])
            setImageURL("");

            alert("Thêm sách thành công 🎉")
        } catch (error) {
            console.log(error);
        }
        setIsLoading(false);

    }

    useEffect(() => {
        const getAllBookks = async () => {
            const response = await axios.get(API_URL + "api/books/allbooks");
            setRecentAddedBooks(response.data.slice(0, 5));
        }

        getAllBookks();
    }, [API_URL]);


    return (
        <div>
            <p className="dashboard-option-title">Thêm một đầu sách mới</p>
            <div className="dashboard-title-line"></div>

            <Form
                // form={form}
                onFinish={addBook}
                layout="vertical"
                style={{ margin: 33, }}
            >
                <Row gutter={16}>
                    <Col span={12}>
                        <Form.Item
                            label="Độc giả"
                            name="borrowerId"
                            rules={[{ required: true, message: 'Vui lòng chọn độc giả' }]}
                            labelStyle={{ fontSize: '20px' }} // Thêm style cho nhãn
                        >
                            <Select
                                placeholder="Chọn độc giả"
                                onChange={setBorrowerId}
                                style={{ fontSize: '16px' }} // Thêm style cho Select component
                            >
                                {allMembers.map(member => (
                                    <Option key={member.value} value={member.value}>{member.text}</Option>
                                ))}
                            </Select>


                        </Form.Item>
                        <Form.Item label="Tên sách" name="bookIds" rules={[{ required: true, message: 'Vui lòng chọn sách' }]}>
                            <Select mode="multiple" placeholder="Chọn sách" onChange={handleBookSelection}>
                                {allBooks.map(book => (
                                    <Option key={book.value} value={book.value}>{book.text}</Option>
                                ))}
                            </Select>
                        </Form.Item>
                        <Form.Item label="Loại mượn" name="transactionType" rules={[{ required: true, message: 'Vui lòng chọn loại mượn' }]}>
                            <Select placeholder="Chọn loại mượn" onChange={setTransactionType}>
                                {transactionTypes.map(type => (
                                    <Option key={type.value} value={type.value}>{type.text}</Option>
                                ))}
                            </Select>
                        </Form.Item>
                    </Col>
                    <Col span={12}>

                        <Form.Item label="Từ ngày" name="fromDate" rules={[{ required: true, message: 'Vui lòng chọn từ ngày' }]}>
                            <DatePicker style={{ width: 640 }} placeholder="Chọn từ ngày" onChange={date => setFromDate(date)} />
                        </Form.Item>
                        <Form.Item label="Đến ngày" name="toDate" rules={[{ required: true, message: 'Vui lòng chọn đến ngày' }]}>
                            <DatePicker style={{ width: 640 }} placeholder="Chọn đến ngày" onChange={date => setToDate(date)} />
                        </Form.Item>
                    </Col>

                </Row>

                <Form.Item
                    style={{
                        paddingTop: 24,
                    }}
                >
                    <Button type="primary" htmlType="submit" loading={isLoading}>Submit</Button>
                </Form.Item>
            </Form>

            <div>
                {/* <Modal open={openModal} onClose={(e) => setOpenModal(false)}>
                    <Card className="conf_modal">
                        <CardContent>
                            <h2>Thêm thể loại</h2>
                        </CardContent>
                        <CardActions className="conf_modal_actions">

                            <TextField
                                label="Tên thể loại"
                                value={categoryName}
                                onChange={(e) => setCategoryName(e.target.value)}
                                fullWidth
                            />

                        </CardActions>
                        <div className="button-modal">
                            <Button className="button-modal" variant="contained" color="error" onClick={() => setOpenModal(false)}>
                                Hủy bỏ
                            </Button>
                            <Button className="button-modal" variant="contained" color="success" onClick={addCate}>
                                Lưu
                            </Button>
                        </div>
                    </Card>
                </Modal> */}
            </div>

            <div>
                <p className="dashboard-option-title">Sách mới thêm gần đây</p>
                <div className="dashboard-title-line"></div>
                <table className='admindashboard-table'>
                    <tr>
                        <th>STT</th>
                        <th>Tên sách</th>
                        <th>Ngày thêm</th>
                    </tr>
                    {
                        recentAddedBooks.map((book, index) => {
                            return (
                                <tr key={index}>
                                    <td>{index + 1}</td>
                                    <td>{book.bookName}</td>
                                    <td>{book.createdAt}</td>
                                </tr>
                            )
                        })
                    }
                </table>
            </div>
        </div>
    )
}

export default AddBook;
