import React, { useEffect, useState } from 'react'
import './BookList.css'
import axios from 'axios'
import Loading from "../Loader/Loader";
import { Link } from 'react-router-dom';
import Footer from '../Footer/Footer'
import BookApi from "../../callAPI/BookApi"
import CateController from '../../Controller/CateController';

function ListCate() {
    const API_URL = process.env.REACT_APP_API_URL

    const [listCate, setListCate] = useState([])
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        setLoading(true)
        const getListCate = async () => {
            try {

                const response = await CateController.getAllCates()
                setListCate(response.data);
                setLoading(false);
                console.log(listCate);
            } catch (err) {
                setLoading(false);
                console.log('Error when get list book =>', err)
            }
        }
        getListCate()
    }, [])
    useEffect(() => {
        const fetchData = async () => {
            try {
                const transactions = await CateController.getTransByUser(user.user._id);
                console.log("user trans", transactions)
                const activeTransactions = transactions.filter(transaction => transaction && transaction.transactionStatus && transaction.transactionStatus === "Active");
                setAllTransactions(activeTransactions);

            } catch (error) {
                console.error('Error fetching transactions:', error);
            }
        };
        fetchData();     // Gọi fetchData để lấy dữ liệu

    }, []);

    if (loading) return <Loading />;

    return (
        <div className="block-page">
            <div className="books-page">
                <div className="title">
                    <label htmlFor="category">Danh sách thể loại</label><br />
                </div>

                <div className="books">
                    {listCate && listCate.map((item) => (
                        <div className="book-card" key={item}>
                            <Link to={`/category/${item}`}>
                                <p className="bookcard-title"><b>{item}</b></p>
                            </Link>
                        </div>
                    ))}
                </div>

            </div>

            <Footer />
        </div>


    )
}


export default ListCate
