import React, { useEffect, useContext, useState } from 'react';

// import "../AdminDashboard.css";
import axios from 'axios';
import { AuthContext } from '../../../../Context/AuthContext';
import { Dropdown } from 'semantic-ui-react';
import { UploadOutlined } from '@ant-design/icons';

import {
    ref,
    uploadBytes,
    getDownloadURL,
    listAll
} from "firebase/storage";
import { storage } from "../../../../firebase";
import { Select, DatePicker, Table, Typography, Button, Form, Row, Col, message, Upload, Input } from 'antd';
const { Option } = Select;
const { Title } = Typography;
function Info() {
   
    const [form] = Form.useForm();
    const onFinish = (values) => {
        setIsLoading(true);
        // Gửi dữ liệu đi
        console.log('Received values:', values);
        setIsLoading(false);
    };



    useEffect(() => {
        console.log(imageUpload);

    }, [imageUpload])

    const handleImageUpload = (info) => {
        const { file } = info;

        const reader = new FileReader();
        reader.onload = (e) => {
            setSelectedImage(e.target.result);
        };

        if (file) {
            reader.readAsDataURL(file);
        }
    };

    const addCate = async (e) => {
        e.preventDefault();

        const CateData = {
            categoryName: categoryName
        }

        try {
            const response = await axios.post(API_URL + "api/categories/addcategory", CateData)

            alert("Thêm thể loại '" + categoryName + "' thành công 🎉");
            setOpenModal(false);
            setCategoryName("");
        } catch (error) {
            console.log(error);
        }
    }

    const addBook = async (e) => {
        e.preventDefault();
        setIsLoading(true);

        if (imageUpload == null) return;
        const imageRef = ref(storage, `image/${imageUpload.name}`);
        uploadBytes(imageRef, imageUpload).then((snapshot) => {
            getDownloadURL(snapshot.ref).then((url) => {
                setImageURL(url);
            });
        });


        const BookData = {
            bookName: bookName,
            alternateTitle: alternateTitle,
            author: author,
            bookCountAvailable: bookCountAvailable,
            language: language,
            publisher: publisher,
            categories: selectedCategories,
            isAdmin: user.isAdmin,
            image_url: imageUpload.name
        }

        try {
            const response = await axios.post(API_URL + "api/books/addbook", BookData)
            if (recentAddedBooks.length >= 5) {
                recentAddedBooks.splice(-1)
            }
            setRecentAddedBooks([response.data, ...recentAddedBooks])
            setBookName("")
            setAlternateTitle("")
            setAuthor("")
            setBookCountAvailable(null)
            setLanguage("")
            setPublisher("")
            setSelectedCategories([])
            setImageURL("");

            alert("Thêm sách thành công 🎉")
        } catch (error) {
            console.log(error);
        }
        setIsLoading(false);

    }

    useEffect(() => {
        const getAllBookks = async () => {
            const response = await axios.get(API_URL + "api/books/allbooks");
            setRecentAddedBooks(response.data.slice(0, 5));
        }

        getAllBookks();
    }, [API_URL]);


    return (
        <div>
            <p className="dashboard-option-title">Thông tin cá nhân</p>
            <div className="dashboard-title-line"></div>
            <Form form={form} onFinish={onFinish} layout="vertical"
                style={{ margin: 33, }}>
                <Row gutter={16}>
                    <Col span={8}>
                        <Form.Item name="userType" label="User Type" rules={[{ required: true, message: 'Please select user type!' }]}>
                            <Select placeholder='User Type' fluid selection onChange={(value) => setUserType(value)}>
                                {/* Render options */}
                            </Select>
                        </Form.Item>

                        <Form.Item name="userFullName" label="Họ và tên" rules={[{ required: true, message: 'Please enter full name!' }]}>
                            <Input />
                        </Form.Item>

                        <Form.Item name={userType === "Student" ? "admissionId" : "employeeId"} label="Mã thẻ" rules={[{ required: true, message: 'Please enter ID!' }]}>
                            <Input />
                        </Form.Item>
                    </Col>
                    <Col span={8}>
                        <Form.Item name="mobileNumber" label="Số điện thoại" rules={[{ required: true, message: 'Please enter mobile number!' }]}>
                            <Input />
                        </Form.Item>

                        <Form.Item name="gender" label="Giới tính" rules={[{ required: true, message: 'Please select gender!' }]}>
                            <Select placeholder='User Type' fluid selection value={gender} onChange={(value) => setGender(value)}>
                                {/* Render options */}
                            </Select>
                        </Form.Item>

                        <Form.Item name="dob" label="Ngày sinh" rules={[{ required: true, message: 'Please select date of birth!' }]}>
                            <DatePicker placeholderText="dd/MM/YYYY" format="DD/MM/YYYY" />
                        </Form.Item>
                    </Col>
                    <Col span={8}>
                        <Form.Item name="address" label="Địa chỉ" rules={[{ required: true, message: 'Please enter address!' }]}>
                            <Input />
                        </Form.Item>

                        <Form.Item name="email" label="Email" rules={[{ required: true, message: 'Please enter email!', type: 'email' }]}>
                            <Input />
                        </Form.Item>

                        <Form.Item name="password" label="Mật khẩu" rules={[{ required: true, message: 'Please enter password!' }]}>
                            <Input.Password />
                        </Form.Item>
                    </Col>

                </Row>

                <Form.Item
                    style={{
                        paddingTop: 24,
                    }}
                >
                    <Button type="primary" htmlType="submit" loading={isLoading}>
                        XÁC NHẬN
                    </Button>
                </Form.Item>
            </Form>

        </div>
    )
}

export default Info;
