import { React, useState, useRef, useEffect, useContext } from 'react'
import { Link } from 'react-router-dom'
import './Header.css';
import MenuIcon from '@material-ui/icons/Menu';
import ClearIcon from '@material-ui/icons/Clear';
import { FaSearch } from "react-icons/fa";
import { AuthContext } from "../../Context/AuthContext.js"
import { useHistory } from "react-router-dom";
import Navbar from "../Navbar/Navbar";
import io from 'socket.io-client';
import {
    Avatar,
    Badge,
    Drawer,
    Button,
    List,
    Skeleton,
    Collapse,
    Typography

} from 'antd';
import { BellOutlined } from '@ant-design/icons';
const { Panel } = Collapse;
const { Text } = Typography;

const count = 10;
const fakeDataUrl = `https://randomuser.me/api/?results=${count}&inc=name,gender,email,nat,picture&noinfo`;
function Header() {
    const [menutoggle, setMenutoggle] = useState(false)
    const history = useHistory();
    const [initLoading, setInitLoading] = useState(true);
    const [loading, setLoading] = useState(false);
    const [data, setData] = useState([]);
    const [list, setList] = useState([]);
    const [open, setOpen] = useState(false);
    const [countNotify, setCountNotify] = useState(0);
    const [notifyStaff, setNotifyStaff] = useState(false);
    const [notifyUser, setNotifyUser] = useState(false);
    const [socket, setSocket] = useState(null);
    // lay count  Notify


    useEffect(() => {
        const fetchData = async () => {
            try {
                if (user.user.isAdmin) {
                    const response = await fetch(`http://localhost:5000/api/notify/countSaft/${user.user._id}`); // Thay đổi URL tùy thuộc vào đường dẫn của API
                    if (!response.ok) {
                        throw new Error('Failed to fetch books');
                    }
                    const data = await response.json();
                    setCountNotify(data); // Cập nhật state allBooks với dữ liệu từ API
                }
                else {
                    const response = await fetch(`http://localhost:5000/api/notify/countUesr/${user.user._id}`); // Thay đổi URL tùy thuộc vào đường dẫn của API
                    if (!response.ok) {
                        throw new Error('Failed to fetch books');
                    }
                    const data = await response.json();
                    setCountNotify(data); // Cập nhật state allBooks với dữ liệu từ API
                } // Cập nhật state allBooks với dữ liệu từ API
            } catch (error) {
                console.error('Error fetching books:', error);
            }
        };

        fetchData(); // Gọi fetchData để lấy dữ liệu
        const newSocket = io('http://localhost:4000'); // Thay đổi URL phù hợp với máy chủ của bạn
        setSocket(newSocket);

        // Lắng nghe sự kiện từ máy chủ
        newSocket.on('dataChanged', () => {
            // Khi có sự thay đổi, gọi lại fetchData để cập nhật số lượng thông báo
            fetchData();
        });

        // Cleanup function để đóng kết nối khi component unmount
        return () => {
            newSocket.disconnect();
        };
    }, []);
    //  lấy thông tin Notify
    console.log(countNotify)
    useEffect(() => {
        const fetchData = async () => {
            try {
                if (user.user.isAdmin) {
                    const response = await fetch(`http://localhost:5000/api/notify/staff-notify/${user.user._id}`); // Thay đổi URL tùy thuộc vào đường dẫn của API
                    if (!response.ok) {
                        throw new Error('Failed to fetch books');
                    }
                    const data = await response.json();
                    setNotifyStaff(data); // Cập nhật state allBooks với dữ liệu từ API
                }
                else {
                    const response = await fetch(`http://localhost:5000/api/notify/user-notify/${user.user._id}`); // Thay đổi URL tùy thuộc vào đường dẫn của API
                    if (!response.ok) {
                        throw new Error('Failed to fetch books');
                    }
                    const data = await response.json();
                    setNotifyUser(data); // Cập nhật state allBooks với dữ liệu từ API
                }


            } catch (error) {
                console.error('Error fetching books:', error);
            }
        };

        fetchData(); // Gọi fetchData để lấy dữ liệu

    }, []);
    const handleNotifyClick = async (notify) => {
        try {
            // Gọi API để cập nhật NotifyStatus của thông báo
            await fetch(`http://localhost:5000/api/notify/update/${notify._id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ NotifyStatus: 0 }),
            });

            // Cập nhật lại danh sách thông báo trên giao diện
            if (user.user.isAdmin) {
                const response = await fetch(`http://localhost:5000/api/notify/countSaft/${user.user._id}`); // Thay đổi URL tùy thuộc vào đường dẫn của API
                if (!response.ok) {
                    throw new Error('Failed to fetch books');
                }
                const data = await response.json();
                setCountNotify(data); // Cập nhật state allBooks với dữ liệu từ API
            }
            else {
                const response = await fetch(`http://localhost:5000/api/notify/countUesr/${user.user._id}`); // Thay đổi URL tùy thuộc vào đường dẫn của API
                if (!response.ok) {
                    throw new Error('Failed to fetch books');
                }
                const data = await response.json();
                setCountNotify(data); // Cập nhật state allBooks với dữ liệu từ API
            } // Cập nhật state allBooks với dữ liệu từ API
        } catch (error) {
            console.error('Error updating notify:', error);
        }
    };

    console.log("dem", countNotify)
    // console.log("dem", notifyUser)

    const onChange = (key) => {
        console.log(key);
