                                label="Tên sách"
                                name="bookName"
                                rules={[{ required: true, message: 'Vui lòng nhập tên sách!' }]}
                            >
                                <Input value={book ? book.bookName : ''} onChange={handlebookNameChange} />
                            </Form.Item>



                            <Form.Item
                                label="Tên tác giả"
                                name="author"
                                rules={[{ required: true, message: 'Vui lòng nhập tên tác giả!' }]}

                            >
                                <Input value={author} onChange={handleAuthorChange} />
                            </Form.Item>
                            <Form.Item label="Ngôn ngữ" name="language">
                                <Select
                                    placeholder="Chọn ngôn ngữ"
                                    onChange={setLanguage}
                                    style={{ fontSize: '16px' }}
                                    showSearch
                                    optionFilterProp="children"
                                    filterOption={(input, option) =>
                                        option.children && option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                                    }
                                >
                                    <Select.Option value="Vi">Tiếng Việt</Select.Option>
                                    <Select.Option value="En">Tiếng Anh</Select.Option>
                                </Select>
                            </Form.Item>
                            <Form.Item label="Nhà xuất bản" name="publisher">
                                <Input value={publisher} onChange={handlePublisherChange} />
                            </Form.Item>

                        </Col>
                        <Col span={8}>

                            <Form.Item
                                label="Số lượng sách sẵn có"
                                name="bookCount"
                                rules={[{ required: true, message: 'Vui lòng nhập số lượng sách sẵn có!' }]}
                            >
                                <Input value={bookCount} onChange={handleBookCountChange} type="number" />
                            </Form.Item>

                            <Form.Item
                                name="categoryName"
                                label="Chọn thể loại"
                                rules={[{ required: true, message: 'Vui lòng chọn độc giả!' }]}
                            >
                                <Select
                                    placeholder="Chọn thể loại"
                                    onChange={setCategoryName}
                                    style={{ fontSize: '16px' }}
                                    showSearch
                                    optionFilterProp="children"
                                    filterOption={(input, option) =>
                                        option.children && option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                                    }
                                >
                                    {allCategories.map(category => (
                                        <Select.Option key={category.categoryName} value={category.categoryName}>
                                            {category.categoryName}
                                        </Select.Option>
                                    ))}
                                </Select>
                            </Form.Item>




                            <Form.Item label="Tiêu đề thay thế" name="description">

                                <Input.TextArea value={description} onChange={handledescriptionChange} rows={4} />
                            </Form.Item>



                        </Col>
                        <Col span={8}>


                            <Form.Item
                                label="Ảnh bìa"
                                name="coverImage"
                                rules={[{ required: true, message: 'Vui lòng tải lên ảnh bìa sách!' }]}
                            >

                                {/* <Upload
                                // action="https://run.mocky.io/v3/435e224c-44fb-4773-9faf-380c5e6a2188"
                                listType="picture-card"
                                fileList={fileList}
                                onPreview={handlePreview}
                                onChange={handleChange}
                            >
                                {fileList.length >= 8 ? null : uploadButton}
