import React, { useContext, useEffect, useState } from 'react'
import './BookList.css'
import axios from 'axios'
import Loading from "../Loader/Loader";
import { Link } from 'react-router-dom';
import { useParams } from 'react-router-dom';
import { FaArrowLeft } from "react-icons/fa";
import { useHistory } from "react-router-dom"
import Footer from '../Footer/Footer'
import {
    Button, Modal,
    Cascader,
    DatePicker,
    Form,
    Input,
    InputNumber,
    Mentions,
    Select,
    TreeSelect,
    Breadcrumb,
    Layout,
    Card

} from 'antd';
import getUrlImage from "../../Pages/getURLimage"
import TransController from '../../Controller/TransactionController';
import { AuthContext } from '../../Context/AuthContext.js';
// import Breadcrumb from '../Breadcrumb/Breadcrumb';
const { Header, Content, Sider } = Layout;
const { Meta } = Card;
function BookList() {
    const [fromDate, setFromDate] = useState(null)
    const [toDate, setToDate] = useState(null);
    const [book, setBook] = useState(null);
    const { category } = useParams();
    const [listBook, setListbook] = useState([])
    const [loading, setLoading] = useState(false);
    const history = useHistory();
    const [isModalOpen, setIsModalOpen] = useState(false);
    const { user } = useContext(AuthContext)

    const handleOk = () => {
        setIsModalOpen(false);
    };
    const handleCancel = () => {
        setIsModalOpen(false);
    };

    const API_URL = process.env.REACT_APP_API_URL

    useEffect(() => {
        setLoading(true)
        const getListBook = async () => {
            try {
                if (category !== "") {
                    const response = await axios.get(API_URL + 'api/books/getbycate/' + category)
                    setListbook(response.data);

                } else {
                    const response = await axios.get(API_URL + 'api/books/allbooks')
                    setListbook(response.data);
                }



                setLoading(false);

            } catch (err) {
                setLoading(false);
                console.log('Error when get list book =>', err)
            }
        }
        getListBook()
    }, [API_URL])
    const handleBorrowClick = (book) => {
        setBook(book);
        setIsModalOpen(true);
    };
    // add transactions
    const addTransaction = async (values) => {
        try {
            setLoading(true);

            // Extracting form values
            const { borrowerId, bookIds, transactionType, fromDate, toDate } = values;
            const transactionData = {
                books: bookIds,
                borrowerId: borrowerId,
                transactionType: transactionType,
                fromDate: fromDate,
                toDate: toDate,
                returnDate: null
                // isAdmin: user.isAdmin,
            };

            // Adding transaction
            await TransController.addTransaction(transactionData);

            // Alerting success message
            alert('T·∫°o phi·∫øu m∆∞·ª£n th√†nh c√¥ng üéâ');
            //   } else {
            //     alert('Cu·ªën s√°ch n√†y ƒë√£ h·∫øt ho·∫∑c kh√¥ng ph√π h·ª£p lo·∫°i giao d·ªãch');
            //   }
            // } else {
            //   alert('Vui l√≤ng ƒëi·ªÅn ƒë·∫ßy ƒë·ªß th√¥ng tin');
            // }
        } catch (err) {
            console.log(err);
            alert('ƒê√£ x·∫£y ra l·ªói khi t·∫°o phi·∫øu m∆∞·ª£n');
        } finally {
            setLoading(false);
        }
    };
    const handleUpdateTransaction = async (transactionId) => {
        try {
            const data = {
                transactionStatus: "True"
            };
            // G·ªçi h√†m updateTransaction t·ª´ TransController ƒë·ªÉ c·∫≠p nh·∫≠t giao d·ªãch
            const updatedTransaction = await TransController.updateTransaction(transactionId, data);
            const Transactions = await TransController.getAllTrans();

            // L·ªçc danh s√°ch c√°c giao d·ªãch c√≥ tr·∫°ng th√°i l√† "Active"
            // const activeTransactions = allTransactions.filter(transaction => transaction && transaction.transactionStatus === "Active");

            console.log('Transaction updated:', updatedTransaction);
            alert('c·∫≠p nh·∫≠t th√†nh c√¥ng üéâ');
            const activeTransactions = Transactions.filter(transaction => transaction && transaction.transactionStatus && transaction.transactionStatus === "Active");
            console.log("thanh", Transactions)

        } catch (error) {
            console.error('Error updating transaction:', error);
        }
    };

    const handleOkModal = () => {
        // Thu th·∫≠p th√¥ng tin t·ª´ c√°c tr∆∞·ªùng nh·∫≠p li·ªáu trong modal
        const formData = {
            borrowerId: user.user._id, // Thay b·∫±ng th√¥ng tin th·ª±c t·∫ø
            bookIds: book._id, // S·ª≠ d·ª•ng _id c·ªßa s√°ch ƒë√£ ch·ªçn
            transactionType: 'Muon', // C√≥ th·ªÉ c·∫ßn ƒëi·ªÅu ch·ªânh t√πy theo y√™u c·∫ßu c·ªßa h·ªá th·ªëng
            fromDate: fromDate,
            toDate: toDate
        };

        // G·ªçi h√†m th√™m giao d·ªãch m·ªõi
        addTransaction(formData);
        // ƒê√≥ng modal sau khi th√™m giao d·ªãch
        setIsModalOpen(false);
    };
    if (loading) return <Loading />;

    return (
        <>
            <Content
                style={{
                    padding: 24,
                    margin: 20,
                    minHeight: 280,
                    width: 300,

                }}
            >

                <div className="block-page">

                    <div className="books-page">
                        <div >
                            <button type='button' className='addbook-submit' onClick={() => history.push("/books")}>
                                <FaArrowLeft size={22} />
                                <span className='fs-18 fw-6'>Tr·ªü l·∫°i</span>
                            </button>
                            <Breadcrumb
                                style={{ borderWidth: '10px' }}
                                items={[
                                    {
                                        title: 'Home',
                                    },
                                    {
                                        title: <a href="">Application Center</a>,
                                    },
                                    {
                                        title: <a href="">Application List</a>,
                                    },
                                    {
                                        title: 'An Application',
                                    },
                                ]}
                            />
                        </div>


                        <div className="title">
                            <label htmlFor="language">S√°ch theo th·ªÉ lo·∫°i "{category}"</label><br />
                        </div>
                        <div className="books">


                            {listBook.map((item) => {
                                return (
                                    <>
                                        {/* <div className="book-card"> */}
                                            {/*    <img src={getUrlImage(item.image_url)} alt="" href={`/book/${item._id}`} ></img>
                                            <Link to={`/book/${item._id}`}>

                                                <p className="bookcard-title"><b>{item.bookName}</b></p>

                                            </Link>
                                            <p className="bookcard-author"> {item.author}</p>
                                            <p className="bookcard-author">{item.publisher}</p>

                                            <div>
                                                <div className="bookcard-category">
                                                    <p>{item.categories}</p>
                                                </div>
                                                <div className="bookcard-select">
                                                    <Button onClick={() => handleBorrowClick(item)}>M∆∞·ª£n</Button>

                                                </div>
                                            </div>
                                            <div className="bookcard-emptybox"></div> */}
                                            <Card
                                                hoverable
                                                style={{
                                                    width: 240,
                                                    height
                                                }}
                                                cover={<img alt="example" src="https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png" />}
                                            >
                                                <Meta title="Europe Street beat" description="www.instagram.com" />
                                            </Card>
                                        {/* </div> */}

                                    </>


                                )
                            })}
                        </div>
                    </div>
                    <Modal title="M∆∞·ª£n s√°ch" open={isModalOpen} onOk={handleOkModal} onCancel={handleCancel}>
                        <Form
                            // form={form}

                            layout="vertical"
                            style={{ margin: 33, }}
                        >
                            <Form.Item label="M∆∞·ª£n s√°ch" name="book" rules={[{ required: true, message: 'Vui l√≤ng ch·ªçn t·ª´ ng√†y' }]}>
                                <Input style={{ width: 400 }} placeholder={book ? book.bookName : ''} value={book ? book._id : ''} readOnly />
                            </Form.Item>
                            <Form.Item label="T·ª´ ng√†y" name="fromDate" rules={[{ required: true, message: 'Vui l√≤ng ch·ªçn t·ª´ ng√†y' }]}>
                                <DatePicker style={{ width: 400 }} placeholder="Ch·ªçn t·ª´ ng√†y" onChange={date => setFromDate(date)} />
                            </Form.Item>
                            <Form.Item label="ƒê·∫øn ng√†y" name="toDate" rules={[{ required: true, message: 'Vui l√≤ng ch·ªçn ƒë·∫øn ng√†y' }]}>
                                <DatePicker style={{ width: 400 }} placeholder="Ch·ªçn ƒë·∫øn ng√†y" onChange={date => setToDate(date)} />
                            </Form.Item>

                        </Form>
                    </Modal>

                    <Footer />
                </div>
            </Content>

        </>

    )
}

export default BookList
