import React, { useContext, useEffect, useState } from 'react'
import "../AdminDashboard.css"
import axios from "axios"
import { Dropdown } from 'semantic-ui-react'
import '../../Member/MemberDashboard.css'
import moment from "moment"
import { AuthContext } from '../../../../Context/AuthContext'
import { Select } from 'antd';
import TransController from '../../../../Controller/TransactionController';
import UserController from '../../../../Controller/UserController';
import BookController from '../../../../Controller/bookController';
const { Option } = Select;


function Return() {

    const API_URL = process.env.REACT_APP_API_URL
    const { user } = useContext(AuthContext)

    const [allTransactions, setAllTransactions] = useState([])
    const [ExecutionStatus, setExecutionStatus] = useState(null) /* For triggering the tabledata to be updated */
    const [allMembers, setAllMembers] = useState([]);
    const [borrowerId, setBorrowerId] = useState(null)
    const [allTransactionsTrue, setAllTransactionsTrue] = useState([])
    const [allTransactionsFalse, setAllTransactionsFalse] = useState([])

    // lay danh sach transaction chua xu ly
    useEffect(() => {
        const fetchData = async () => {
            try {
                let transactions;
                // if (borrowerId === null) {
                transactions = await TransController.getAllTrans();
                // } else {
                //   transactions = await TransController.getTransByUser(borrowerId);
                // }

                const activeTransactions = transactions.filter(transaction => transaction && transaction.transactionStatus && transaction.transactionStatus === "Active");
                setAllTransactions(activeTransactions);
            } catch (error) {
                console.error('Error fetching transactions:', error);
            }
        };
        fetchData();     // G·ªçi fetchData ƒë·ªÉ l·∫•y d·ªØ li·ªáu

    }, []); // useEffect s·∫Ω ch·ªâ ch·∫°y m·ªôt l·∫ßn sau khi render l·∫ßn ƒë·∫ßu ti√™n
    const fetchUserTransactions = async (userId) => {
        try {
            const transactions = await TransController.getTransByUser(userId);
            console.log("user trans", transactions)
            setAllTransactions(transactions);
        } catch (error) {
            console.error('Error fetching user transactions:', error);
        }
    };
    // lay danh sach nguoi dung khong phai la admin
    useEffect(() => {
        const fetchData = async () => {

            try {
                const allUser = await UserController.getAllUsers();
                // L·ªçc danh s√°ch c√°c giao d·ªãch c√≥ tr·∫°ng th√°i l√† "Active"
                const Users = allUser.filter(user => user && user.isAdmin !== true);
                setAllMembers(Users); // ƒê·∫∑t danh s√°ch c√°c giao d·ªãch "Active" v√†o state
            } catch (error) {
                console.error('Error fetching transactions:', error);
            }
        };

        fetchData(); // G·ªçi fetchData ƒë·ªÉ l·∫•y d·ªØ li·ªáu

    }, []);
    const handleSelectUser = (userId) => {
        setBorrowerId(userId)
        fetchUserTransactions(userId);
    };
    const handleUpdateTransaction = async (transactionId) => {
        try {
            const data = {
                transactionStatus: "True"
            };
            // G·ªçi h√†m updateTransaction t·ª´ TransController ƒë·ªÉ c·∫≠p nh·∫≠t giao d·ªãch
            const updatedTransaction = await TransController.updateTransaction(transactionId, data);
            const Transactions = await TransController.getAllTrans();

            // L·ªçc danh s√°ch c√°c giao d·ªãch c√≥ tr·∫°ng th√°i l√† "Active"
            // const activeTransactions = allTransactions.filter(transaction => transaction && transaction.transactionStatus === "Active");
            const activeTransactions = Transactions.filter(transaction => transaction && transaction.transactionStatus && transaction.transactionStatus === "Active");
            console.log("thanh", Transactions)

            setAllTransactions(activeTransactions);

            const allBooks = await BookController.getAllBooks();
            setAllBooks(allBooks);
            console.log('Transaction updated:', updatedTransaction);
            alert('c·∫≠p nh·∫≠t th√†nh c√¥ng üéâ');
        } catch (error) {
            console.error('Error updating transaction:', error);
        }
    };


    //Fetching all Members
    useEffect(() => {
        const getMembers = async () => {
            try {
                const response = await axios.get(API_URL + 'api/users/allmembers');
                const allMembers = response.data.map(member => ({
                    value: member._id,
                    text: `${member.userFullName} [${member.userType === 'Student' ? member.admissionId : member.employeeId}]`,
                }));
                setAllMembers(allMembers);
            } catch (error) {
                console.log('Error fetching members:', error);
            }
        };
        getMembers();
    }, [API_URL]);


    /* Getting all active transactions */
    useEffect(() => {
        const getAllTransactions = async () => {
            try {
                const response = await axios.get(API_URL + "api/transactions/all-transactions")
                setAllTransactions(response.data.sort((a, b) => Date.parse(a.toDate) - Date.parse(b.toDate)).filter((data) => {
                    return data.transactionStatus === "Active"
                }))
                console.log("Okay")
                setExecutionStatus(null)
            }
            catch (err) {
                console.log(err)
            }
        }
        getAllTransactions()
    }, [API_URL, ExecutionStatus])


    const returnBook = async (transactionId, borrowerId, bookId, due) => {
        try {
            /* Setting return date and transactionStatus to completed */
            await axios.put(API_URL + "api/transactions/update-transaction/" + transactionId, {
                isAdmin: user.isAdmin,
                transactionStatus: "Completed",
                returnDate: moment(new Date()).format("MM/DD/YYYY")
            })

            /* Getting borrower points alreadt existed */
            const borrowerdata = await axios.get(API_URL + "api/users/getuser/" + borrowerId)
            const points = borrowerdata.data.points

            /* If the number of days after dueDate is greater than zero then decreasing points by 10 else increase by 10*/
            if (due > 0) {
                await axios.put(API_URL + "api/users/updateuser/" + borrowerId, {
                    points: points - 10,
                    isAdmin: user.isAdmin
                })
            }
            else if (due <= 0) {
                await axios.put(API_URL + "api/users/updateuser/" + borrowerId, {
                    points: points + 10,
                    isAdmin: user.isAdmin
                })
            }

            const book_details = await axios.get(API_URL + "api/books/getbook/" + bookId)
            await axios.put(API_URL + "api/books/updatebook/" + bookId, {
                isAdmin: user.isAdmin,
                bookCountAvailable: book_details.data.bookCountAvailable + 1
            })

            /* Pulling out the transaction id from user active Transactions and pushing to Prev Transactions */
            // await axios.put(API_URL + `api/users/${transactionId}/move-to-prevtransactions`, {
            //     userId: borrowerId,
            //     isAdmin: user.isAdmin
            // })

            setExecutionStatus("Completed");
            alert("ƒê√£ l∆∞u th√¥ng tin tr·∫£ s√°ch th√†nh c√¥ng!");
        }
        catch (err) {
            console.log(err)
        }
    }

    const convertToIssue = async (transactionId) => {
        try {
            await axios.put(API_URL + "api/transactions/update-transaction/" + transactionId, {
                transactionType: "Issued",
                isAdmin: user.isAdmin
            })
            setExecutionStatus("Completed");
            alert("Chuy·ªÉn tr·∫°ng th√°i 'ƒê√£ m∆∞·ª£n' th√†nh c√¥ng üéÜ")
        }
        catch (err) {
            console.log(err)
        }
    }


    return (
        <div>
            <div className='semanticdropdown returnbook-dropdown'>
                <Select
                    placeholder="Ch·ªçn ƒë·ªôc gi·∫£"
                    onChange={handleSelectUser}
                    style={{ fontSize: '16px' }} // Th√™m style cho Select component
                >
                    {allMembers.map(member => (
                        <Option key={member.value} value={member.value}>{member.text}</Option>
                    ))}
