import React, { useEffect, useState } from 'react'
import "../AdminDashboard.css"
import axios from "axios"
import { Dropdown } from 'semantic-ui-react'

import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import moment from 'moment';


function AddMember() {
    const API_URL = process.env.REACT_APP_API_URL;
    const [isLoading, setIsLoading] = useState(false);

    const [userFullName, setUserFullName] = useState(null)
    const [admissionId, setAdmissionId] = useState(null)
    const [employeeId, setEmployeeId] = useState(null)
    const [address, setAddress] = useState(null)
    const [email, setEmail] = useState(null)
    const [password, setPassword] = useState(null)
    const [mobileNumber, setMobileNumber] = useState(null)
    const [recentAddedMembers, setRecentAddedMembers] = useState([])
    const [userType, setUserType] = useState(null)
    const [gender, setGender] = useState(null)
    const [age, setAge] = useState(null)
    const [dob, setDob] = useState(null)
    const [dobString, setDobString] = useState(null)

    const genderTypes = [
        { value: "Male", text: "Male" },
        { value: "Female", text: "Female" }
    ]

    const userTypes = [
        { value: 'Staff', text: 'Nhân viên' },
        { value: 'Student', text: 'Sinh viên' }
    ]


    const addMember = async (e) => {
        e.preventDefault();
        setIsLoading(true);


        if (userFullName !== null && userType !== null && age !== null && dob !== null & gender !== null && address !== null && mobileNumber !== null && email !== null && password !== null) {
            const userData = {
                userType: userType,
                userFullName: userFullName,
                admissionId: admissionId,
                employeeId: employeeId,
                age: age,
                dob: dobString,
                gender: gender,
                address: address,
                mobileNumber: mobileNumber,
                email: email,
                password: password
            }

            try {
                const response = await axios.post(API_URL + "api/auth/register", userData);
                if (recentAddedMembers.length >= 5) {
                    recentAddedMembers.splice(-1);
                }
                setRecentAddedMembers([response.data, ...recentAddedMembers])
                setUserFullName(null)
                setUserType("Student")
                setAdmissionId(null)
                setEmployeeId(null)
                setAddress(null)
                setMobileNumber(null)
                setEmail(null)
                setPassword(null)
                setGender(null)
                setAge(null)
                setDob(null)
                setDobString(null)
                alert("Thêm thành viên thành công !!");


            } catch (error) {
                console.log(error);
            }
        } else {
            alert("Không được để trống các trường ");
        }
        setIsLoading(false);
    }


    useEffect(() => {
        const getMembers = async () => {
            try {
                const response = await axios.get(API_URL + "api/users/allmembers")
                const recentMembers = await response.data.slice(0, 5)
                setRecentAddedMembers(recentMembers)
            }
            catch (err) {
                console.log(err)
            }
        }
        getMembers()
    }, [API_URL])


    return (
        <div>
            <p className="dashboard-option-title">Thêm người dùng</p>
            <div className="dashboard-title-line"></div>
            <Form
                form={form}
                name="addmember-form"
                onFinish={onFinish}
                labelCol={{ span: 8 }}
                wrapperCol={{ span: 16 }}
            >
                <Form.Item
                    label="User Type"
                    name="userType"
                    rules={[{ required: true, message: 'Please select user type!' }]}
                >
                    <Dropdown
                        placeholder="User Type"
                        onChange={(value) => setUserType(value)}
                    >
                        {/* Render options */}
                    </Dropdown>
                </Form.Item>

                <Form.Item
                    label="Họ và tên"
                    name="userFullName"
                    rules={[{ required: true, message: 'Please enter full name!' }]}
                >
                    <Input />
                </Form.Item>

                <Form.Item
                    label={userType === "Student" ? "Mã thẻ" : "Employee ID"}
                    name={userType === "Student" ? "admissionId" : "employeeId"}
                    rules={[{ required: true, message: 'Please enter ID!' }]}
                >
                    <Input />
                </Form.Item>

                <Form.Item
                    label="Số điện thoại"
                    name="mobileNumber"
                    rules={[{ required: true, message: 'Please enter mobile number!' }]}
                >
                    <Input />
                </Form.Item>

                <Form.Item
                    label="Giới tính"
                    name="gender"
                    rules={[{ required: true, message: 'Please select gender!' }]}
                >
                    <Dropdown
                        placeholder="Gender"
                        onChange={(value) => setGender(value)}
                    >
                        {/* Render options */}
                    </Dropdown>
                </Form.Item>

                <Form.Item
                    label="Ngày sinh"
                    name="dob"
                    rules={[{ required: true, message: 'Please select date of birth!' }]}
                >
                    <DatePicker placeholder="Select Date" />
                </Form.Item>

                <Form.Item
                    label="Địa chỉ"
                    name="address"
                    rules={[{ required: true, message: 'Please enter address!' }]}
                >
                    <Input />
                </Form.Item>

                <Form.Item
                    label="Email"
                    name="email"
                    rules={[{ required: true, message: 'Please enter email!', type: 'email' }]}
                >
                    <Input />
                </Form.Item>

                <Form.Item
                    label="Mật khẩu"
                    name="password"
                    rules={[{ required: true, message: 'Please enter password!' }]}
                >
                    <Input.Password />
                </Form.Item>

                <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
                    <Button type="primary" htmlType="submit" loading={isLoading}>
                        XÁC NHẬN
                    </Button>
                </Form.Item>
            </Form>
            <p className="dashboard-option-title">Mới thêm gần đây</p>
            <div className="dashboard-title-line"></div>
            <table className='admindashboard-table'>
                <tr>
                    <th>STT</th>
                    <th>Loại tài khoản</th>
                    <th>Mã tài khoản</th>
                    <th>Họ và tên</th>
                </tr>
                {
                    recentAddedMembers.map((member, index) => {
                        return (
                            <tr key={index}>
                                <td>{index + 1}</td>
                                <td>{member.userType}</td>
                                <td>{member.userType === "Student" ? member.admissionId : member.employeeId}</td>
                                <td>{member.userFullName}</td>
                            </tr>
                        )
                    })
                }
            </table>
        </div>
    )

}

export default AddMember;
