import React, { useEffect, useContext, useState } from 'react';
import { Button, Table, Modal, Card, Typography, message } from 'antd';
import 'antd/dist/antd.css';
import axios from 'axios';
import { AuthContext } from '../../../../Context/AuthContext';
import getUrlImage from '../../../../Pages/getURLimage';
import Loading from '../../../../Components/Loader/Loader';

const { Column } = Table;

function AddBook() {
    const { user } = useContext(AuthContext);
    const API_URL = process.env.REACT_APP_API_URL;
    const [loading, setLoading] = useState(false);
    const [books, setBooks] = useState([]);
    const [page, setPage] = useState(1);
    const [pageSize, setPageSize] = useState(10);
    const [openModal, setOpenModal] = useState(false);
    const [activeBookID, setActiveBookID] = useState(null);

    const fetchBooks = async () => {
        setLoading(true);
        try {
            const response = await axios.get(API_URL + 'api/books/allbooks');
            setBooks(response.data);
        } catch (error) {
            message.error('Failed to fetch books.');
            console.error('Error fetching books:', error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchBooks();
    }, []);

    const deleteBook = async () => {
        try {
            if (activeBookID !== null) {
                await axios.delete(API_URL + 'api/books/removebook/' + activeBookID, {
                    isAdmin: user.isAdmin,
                });
                message.success('Xóa đầu sách thành công!');
                setOpenModal(false);
                fetchBooks();
            }
        } catch (error) {
            message.error('Đã xảy ra lỗi khi xóa đầu sách.');
            console.error('Error deleting book:', error);
        }
    };

    if (loading) return <Loading />;

    return (
        <div>
            <p className="dashboard-option-title">Tất cả đầu sách</p>
            <div className="dashboard-title-line"></div>
            {books.length > 0 ? (
                <Table dataSource={books} pagination={{ pageSize, current: page, total: books.length }}>
                    <Column
                        title="Ảnh bìa"
                        dataIndex="image_url"
                        key="image_url"
                        render={(imageUrl) => <img className="image-table" src={getUrlImage(imageUrl)} alt="" />}
                    />
                    <Column title="Tên sách" dataIndex="bookName" key="bookName" />
                    <Column title="Thể loại" dataIndex="categories" key="categories" />
                    <Column title="Số lượng" dataIndex="bookCountAvailable" key="bookCountAvailable" />
                    <Column
                        title="Hành động"
                        key="action"
                        render={(text, record) => (
                            <div className="actionsContainer">
                                <Button variant="contained" size="small">
                                    Xem
                                </Button>
                                {user.isAdmin && (
                                    <>
                                        <Button variant="contained" size="small">
                                            Sửa
                                        </Button>
                                        <Button
                                            variant="contained"
                                            color="error"
                                            size="small"
                                            onClick={() => {
                                                setActiveBookID(record._id);
                                                setOpenModal(true);
                                            }}
                                        >
                                            Xóa
                                        </Button>
                                    </>
                                )}
                            </div>
                        )}
                    />
                </Table>
            ) : (
                <Typography variant="h5">Không tìm thấy đầu sách!</Typography>
            )}

            <Modal
                title="Bạn có chắc chắn muốn xóa?"
                visible={openModal}
                onCancel={() => setOpenModal(false)}
                footer={[
                    <Button key="cancel" onClick={() => setOpenModal(false)}>
                        Hủy bỏ
                    </Button>,
                    <Button key="delete" type="primary" danger onClick={deleteBook}>
                        Xóa
                    </Button>,
                ]}
            >
                <p>Xóa đầu sách này sẽ không thể khôi phục lại.</p>
            </Modal>
        </div>
    );
}

export default AddBook;
