import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { Layout, Menu, Typography } from 'antd';
import {
    UserOutlined,
    BookOutlined,
    TeamOutlined,
    UserAddOutlined,
    PoweroffOutlined,
} from '@ant-design/icons';
import './AdminDashboard.css';
import AddTransaction from './Components/AddTransaction';
import AddMember from './Components/AddMember';
import AddBook from './Components/AddBook';
import ManagerBook from './Components/ManagerBook';
import GetMember from './Components/GetMember';
import Return from './Components/Return';

const { Sider } = Layout;
const { Title } = Typography;

function AdminDashboard() {
    const [active, setActive] = useState('addbooks');
    const [collapsed, setCollapsed] = useState(false);

    const history = useHistory();

    const handleLogout = () => {
        localStorage.removeItem('user');
        history.push('/signin');
        window.location.reload();
    };

    return (
        <Layout style={{ minHeight: '100vh' }}>
            <Sider collapsible collapsed={collapsed} onCollapse={setCollapsed}>
                <div className="dashboard-logo">
                    <BookOutlined style={{ fontSize: '32px', marginRight: '8px' }} />
                    <Title level={3} style={{ margin: 0 }}>
                        THƯ VIỆN MTA
                    </Title>
                </div>
                <Menu theme="dark" defaultSelectedKeys={['addbooks']} mode="inline" selectedKeys={[active]}>
                    <Menu.Item key="profile" icon={<UserOutlined />} onClick={() => setActive('profile')}>
                        <span style={{ fontSize: '16px', fontWeight: 'bold' }}>Thông tin cá nhân</span>
                    </Menu.Item>
                    <Menu.Item key="addbook" icon={<BookOutlined />} onClick={() => setActive('addbook')}>
                        <span style={{ fontSize: '16px', fontWeight: 'bold' }}>Thêm sách</span>
                    </Menu.Item>
                    <Menu.Item key="manabook" icon={<BookOutlined />} onClick={() => setActive('manabook')}>
                        <span style={{ fontSize: '16px', fontWeight: 'bold' }}>Quản lý sách</span>
                    </Menu.Item>
                    <Menu.Item key="addtransaction" icon={<ReceiptOutlined />} onClick={() => setActive('addtransaction')}>
                        <span style={{ fontSize: '16px', fontWeight: 'bold' }}>Tạo phiếu mượn</span>
                    </Menu.Item>
                    <Menu.Item key="getmember" icon={<TeamOutlined />} onClick={() => setActive('getmember')}>
                        <span style={{ fontSize: '16px', fontWeight: 'bold' }}>Quản lý độc giả</span>
                    </Menu.Item>
                    <Menu.Item key="addmember" icon={<UserAddOutlined />} onClick={() => setActive('addmember')}>
                        <span style={{ fontSize: '16px', fontWeight: 'bold' }}>Thêm độc giả</span>
                    </Menu.Item>
                    <Menu.Item key="returntransaction" icon={<ReadOutlined />} onClick={() => setActive('returntransaction')}>
                        <span style={{ fontSize: '16px', fontWeight: 'bold' }}>Trả sách</span>
                    </Menu.Item>
                    <Menu.Item key="logout" icon={<PoweroffOutlined />} onClick={handleLogout}>
                        <span style={{ fontSize: '16px', fontWeight: 'bold' }}>Đăng xuất</span>
                    </Menu.Item>
                </Menu>
            </Sider>
            <div className="dashboard-option-content">
                    <div className="dashboard-addbooks-content" style={active !== "addbook" ? { display: 'none' } : {}}>
                        <AddBook />
                    </div>
                    <div className="dashboard-addbooks-content" style={active !== "manabook" ? { display: 'none' } : {}}>
                        < ManagerBook />
                    </div>
                    <div className="dashboard-transactions-content" style={active !== "addtransaction" ? { display: 'none' } : {}}>
                        <AddTransaction />
                    </div>
                    <div className="dashboard-addmember-content" style={active !== "addmember" ? { display: 'none' } : {}}>
                        <AddMember />
                    </div>
                    <div className="dashboard-addmember-content" style={active !== "getmember" ? { display: 'none' } : {}}>
                        <GetMember />
                    </div>
                    <div className="dashboard-addmember-content" style={active !== "returntransaction" ? { display: 'none' } : {}}>
                        <Return />
                    </div>
                </div>
        </Layout>
    );
}

export default AdminDashboard;
