import React, { useContext, useEffect, useState } from 'react'
import './BookList.css'
import axios from 'axios'
import Loading from "../Loader/Loader";
import { Link } from 'react-router-dom';
import { useParams } from 'react-router-dom';
import { FaArrowLeft } from "react-icons/fa";
import { useHistory } from "react-router-dom"
import Footer from '../Footer/Footer'
import {
    Button, Modal,
    Cascader,
    DatePicker,
    Form,
    Input,
    InputNumber,
    Mentions,
    Select,
    TreeSelect,
    Breadcrumb,
    Layout,
    Card,
    theme,
    Image

} from 'antd';
import getUrlImage from "../../Pages/getURLimage"
import TransController from '../../Controller/TransactionController';
import { AuthContext } from '../../Context/AuthContext.js';
const { Header, Content, Sider } = Layout;
const { Meta } = Card;
function BookList() {
    const [fromDate, setFromDate] = useState(null)
    const [toDate, setToDate] = useState(null);
    const [book, setBook] = useState(null);
    const { category } = useParams();
    const [listBook, setListbook] = useState([])
    const [loading, setLoading] = useState(false);
    const history = useHistory();
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [isBookOpen, setIsBookOpen] = useState(false);

    const { user } = useContext(AuthContext)
    const {
        token: { colorBgContainer, borderRadiusLG },
    } = theme.useToken();
    const handleOk = () => {
        setIsModalOpen(false);
    };
    const handleCancel = () => {
        setIsModalOpen(false);
    };
    const handleBookOk = () => {
        setIsBookOpen(false);
    };
    const handleBookCancel = () => {
        setIsBookOpen(false);
    };
    const API_URL = process.env.REACT_APP_API_URL

    useEffect(() => {
        setLoading(true)
        const getListBook = async () => {
            try {
                if (category !== "") {
                    const response = await axios.get(API_URL + 'api/books/getbycate/' + category)
                    setListbook(response.data);

                } else {
                    const response = await axios.get(API_URL + 'api/books/allbooks')
                    setListbook(response.data);
                }

                setLoading(false);

            } catch (err) {
                setLoading(false);
                console.log('Error when get list book =>', err)
            }
        }
        getListBook()
    }, [API_URL])
    const handleBorrowClick = (book) => {
        setBook(book);
        setIsModalOpen(true);
    };
    const handle= (book) => {
        setBook(book);
        setIsModalOpen(true);
    };


    const addTransaction = async (values) => {
        try {
            setLoading(true);

            const { borrowerId, bookIds, transactionType, fromDate, toDate } = values;
            const transactionData = {
                books: bookIds,
                borrowerId: borrowerId,
                transactionType: transactionType,
                fromDate: fromDate,
                toDate: toDate,
                returnDate: null
            };

            await TransController.addTransaction(transactionData);

            alert('Táº¡o phiáº¿u mÆ°á»£n thÃ nh cÃ´ng ðŸŽ‰');

        } catch (err) {
            console.log(err);
            alert('ÄÃ£ xáº£y ra lá»—i khi táº¡o phiáº¿u mÆ°á»£n');
        } finally {
            setLoading(false);
        }
    };

    const handleUpdateTransaction = async (transactionId) => {
        try {
            const data = {
                transactionStatus: "True"
            };

            const updatedTransaction = await TransController.updateTransaction(transactionId, data);
            const Transactions = await TransController.getAllTrans();

            console.log('Transaction updated:', updatedTransaction);
            alert('cáº­p nháº­t thÃ nh cÃ´ng ðŸŽ‰');
            const activeTransactions = Transactions.filter(transaction => transaction && transaction.transactionStatus && transaction.transactionStatus === "Active");
            console.log("thanh", Transactions)

        } catch (error) {
            console.error('Error updating transaction:', error);
        }
    };

    const handleOkModal = () => {
        const formData = {
            borrowerId: user.user._id,
            bookIds: book._id,
            transactionType: 'Muon',
            fromDate: fromDate,
            toDate: toDate
        };

        addTransaction(formData);
        setIsModalOpen(false);
    };
    if (loading) return <Loading />;

    return (
        <>
            <Content
                style={{
                    background: colorBgContainer,
                    borderRadius: borderRadiusLG,
                }}
            >
                <div style={{
                    marginLeft: 20,
                    marginRight: 20,
                }}>
                    <Breadcrumb
                        style={{ borderWidth: '10px' }}
                        items={[
                            {
                                title: 'Home',
                            },
                            {
                                title: <a href="">Application Center</a>,
                            },
                            {
                                title: <a href="">Application List</a>,
                            },
                            {
                                title: 'An Application',
                            },
                        ]}
                    />
                </div>
                <div className="block-page">

                    <div className="books-page">
                        <div className="title">
                            <label htmlFor="language">SÃ¡ch theo thá»ƒ loáº¡i "{category}"</label><br />
                        </div>
                        <div className="books">
                            {listBook.map((item, index) => (
                                <>
                                    {/* <div key={index} className="book-card"> */}

                                    <Card
                                        hoverable
                                        style={{
                                            width: 300,
                                            height: 320
                                            //  margin: '10px'
                                        }}
                                    // cover={<img alt="example"
                                    //     src={item.image_url}
                                    //     style={{
                                    //         Height: '200px',
                                    //         width: 240,
                                    //         objectFit: 'cover'
                                    //     }} />}
                                    >
                                        <Image
                                            width={250}
                                            height={180}
                                            src={item.image_url}
                                        />
                                        <Meta
                                            title={item.bookName}
                                            description={item.author}
                                        />


                                        <Button onClick={() => handleBorrowClick(item)}>MÆ°á»£n</Button>
                                    </Card>



                                    {/* </div> */}

                                </>


                            ))}
                        </div>
                    </div>
                    <Modal title="MÆ°á»£n sÃ¡ch" open={isModalOpen} onOk={handleOkModal} onCancel={handleCancel}>
                        <Form layout="vertical" style={{ margin: 33 }}>
                            <Form.Item label="MÆ°á»£n sÃ¡ch" name="book" rules={[{ required: true, message: 'Vui lÃ²ng chá»n tá»« ngÃ y' }]}>
                                <Input style={{ width: 400 }} placeholder={book ? book.bookName : ''} value={book ? book._id : ''} readOnly />
                            </Form.Item>
                            <Form.Item label="Tá»« ngÃ y" name="fromDate" rules={[{ required: true, message: 'Vui lÃ²ng chá»n tá»« ngÃ y' }]}>
                                <DatePicker style={{ width: 400 }} placeholder="Chá»n tá»« ngÃ y" onChange={date => setFromDate(date)} />
                            </Form.Item>
                            <Form.Item label="Äáº¿n ngÃ y" name="toDate" rules={[{ required: true, message: 'Vui lÃ²ng chá»n Ä‘áº¿n ngÃ y' }]}>
                                <DatePicker style={{ width: 400 }} placeholder="Chá»n Ä‘áº¿n ngÃ y" onChange={date => setToDate(date)} />
                            </Form.Item>
                        </Form>
                    </Modal>
                    <Modal title="MÆ°á»£n sÃ¡ch" open={isModalOpen} onOk={handleBookOk} onCancel={handleBookCancel}>
                        <Card
                            hoverable
                            style={{
                                width: 300,
                                height: 320
                                //  margin: '10px'
                            }}
                        // cover={<img alt="example"
                        //     src={item.image_url}
                        //     style={{
                        //         Height: '200px',
                        //         width: 240,
                        //         objectFit: 'cover'
                        //     }} />}
                        >
                            <Image
                                width={250}
                                height={180}
                                // src={book.image_url}
                            />
                            <Meta
                                // title={book.bookName}
                                // description={book.author}
                            />


                            <Button onClick={() => handleBorrowClick(book)}>MÆ°á»£n</Button>
                        </Card>

                    </Modal>
                    <Footer />
                </div>
            </Content>
        </>
    )
}

export default BookList
