import React, { useEffect, useState } from 'react';
import './BookList.css';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { useHistory } from 'react-router-dom';
import { FaArrowLeft } from 'react-icons/fa';
import { List, Button } from 'antd';
import 'antd/dist/antd.css';
import Loading from '../Loader/Loader';
import Footer from '../Footer/Footer';
import getUrlImage from '../../Pages/getURLimage';

function BookList() {
    const { category } = useParams();
    const [listBook, setListbook] = useState([]);
    const [loading, setLoading] = useState(false);
    const history = useHistory();

    const API_URL = process.env.REACT_APP_API_URL;

    useEffect(() => {
        setLoading(true);
        const getListBook = async () => {
            try {
                let response;
                if (category !== "") {
                    response = await axios.get(API_URL + 'api/books/getbycate/' + category);
                } else {
                    response = await axios.get(API_URL + 'api/books/allbooks');
                }
                setListbook(response.data);
            } catch (err) {
                console.log('Error when get list book =>', err);
            } finally {
                setLoading(false);
            }
        };
        getListBook();
    }, [API_URL, category]);

    if (loading) return <Loading />;

    return (
        <div className="block-page">
            <div className="books-page">
                <Button type="primary" onClick={() => history.push("/books")}>
                    <FaArrowLeft size={22} />
                    <span className="fs-18 fw-6">Trở lại</span>
                </Button>
                <div className="title">
                    <label htmlFor="language">Sách theo thể loại "{category}"</label><br />
                </div>
                <div className="books">
                    <List
                        grid={{ gutter: 16, column: 3 }}
                        dataSource={listBook}
                        renderItem={item => (
                            <List.Item>
                                <div className="book-card">
                                    <img src={getUrlImage(item.image_url)} alt="" />
                                    <Link to={`/book/${item._id}`}>
                                        <p className="bookcard-title"><b>{item.bookName}</b></p>
                                    </Link>
                                    <p className="bookcard-author">{item.author}</p>
                                    <p className="bookcard-author">{item.publisher}</p>
                                    <div>
                                        <div className="bookcard-category">
                                            <p>{item.categories}</p>
                                        </div>
                                        <div className="bookcard-select">
                                            <Button type="primary">Mượn</Button>
                                        </div>
                                    </div>
                                    <div className="bookcard-emptybox"></div>
                                </div>
                            </List.Item>
                        )}
                    />
                </div>
            </div>
            <Footer />
        </div>
    );
}

export default BookList;
