import axios from "axios"
const API_URL = process.env.REACT_APP_API_URL;

const BookApi = {
  GetAllBooks: async () => {
    const res = await axios.get(API_URL+ "api/books/allbooks")
    return res.data
  },
  // http://localhost:5000/api/books/getallcate
  // getAllCate: async () => {
  //   const res = await axios.get("/api/books/getallcate")
  //   return res.data
  // },
  getBookbyID: async (bookID) => {
    const res = await axios.get(API_URL+ `api/books/${bookID}`)
    return res.data
  },
  addBook: async (data) => {
    const res = await axios.post(API_URL+"api/books/addbook", data)
    return res.data
  },
  updateBook: async (bookID, data) => {
    const res = await axios.put(API_URL+ `api/books/updatebook/${bookID}`, data)
    return res.data
  },
  deleteBook: async (bookID) => {
    const res = await axios.delete(API_URL +`/api/books/removebook/${bookID}`)
    return res.data
  },
}

export default BookApi;
