import React, { useState } from 'react';
im
import { Menu, Layout } from 'antd';
import {  UserOutlined,
    BookOutlined,
    ReadOutlined,
    TeamOutlined,
    UserAddOutlined,
    LogoutOutlined,} from '@ant-design/icons'

const { Sider } = Layout;
const { SubMenu } = Menu;

function MemberDashboard() {
  const [active, setActive] = useState("profile");
  const [collapsed, setCollapsed] = useState(false);

  const handleClick = (e) => {
    setActive(e.key);
  };

  const handleCollapse = () => {
    setCollapsed(!collapsed);
  };

  const logout = () => {
    localStorage.removeItem("user");
    // Redirect to SignIn page
    window.location.href = '/signin';
  };

  return (
    <Layout style={{ minHeight: '100vh' }}>
      <Sider collapsible collapsed={collapsed} onCollapse={handleCollapse}>
        <div className="dashboard-logo" style={{ height: 64, display: 'flex', alignItems: 'center', justifyContent: 'center', color: 'white' }}>
          <h2 style={{ margin: 0 }}>THU VIEN MTA</h2>
        </div>
        <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline" onClick={handleClick} selectedKeys={[active]}>
          <Menu.Item key="profile" icon={<UserOutlined />}>
            Thông tin cá nhân
          </Menu.Item>
          <SubMenu key="book" icon={<BookOutlined />} title="Quản lý sách">
            <Menu.Item key="addbook">Thêm sách</Menu.Item>
            <Menu.Item key="manabook">Quản lý sách</Menu.Item>
            <Menu.Item key="addtransaction">Tạo phiếu mượn</Menu.Item>
          </SubMenu>
          <Menu.Item key="getmember" icon={<TeamOutlined />}>
            Quản lý độc giả
          </Menu.Item>
          <Menu.Item key="addmember" icon={<UserAddOutlined />}>
            Thêm độc giả
          </Menu.Item>
          <Menu.Item key="returntransaction" icon={<ReadOutlined />}>
            Trả sách
          </Menu.Item>
          <Menu.Item key="logout" icon={<LogoutOutlined />} onClick={logout}>
            Đăng xuất
          </Menu.Item>
        </Menu>
      </Sider>
    </Layout>
  );
}

export default MemberDashboard;
