import React, { useEffect, useState } from 'react'
import './BookList.css'
import axios from 'axios'
import Loading from "../Loader/Loader";
import { Link } from 'react-router-dom';
import { useParams } from 'react-router-dom';
import { FaArrowLeft } from "react-icons/fa";
import { useHistory } from "react-router-dom"
import Footer from '../Footer/Footer'
import { Button, Modal } from 'antd';
import getUrlImage from "../../Pages/getURLimage"

import Breadcrumb from '../Breadcrumb/Breadcrumb';

function BookList() {

    const { category } = useParams();
    const [listBook, setListbook] = useState([])
    const [loading, setLoading] = useState(false);
    const history = useHistory();
    const [isModalOpen, setIsModalOpen] = useState(false);
    const showModal = () => {
        setIsModalOpen(true);
    };
    const handleOk = () => {
        setIsModalOpen(false);
    };
    const handleCancel = () => {
        setIsModalOpen(false);
    };

    const API_URL = process.env.REACT_APP_API_URL

    useEffect(() => {
        setLoading(true)
        const getListBook = async () => {
            try {
                if (category !== "") {
                    const response = await axios.get(API_URL + 'api/books/getbycate/' + category)
                    setListbook(response.data);

                } else {
                    const response = await axios.get(API_URL + 'api/books/allbooks')
                    setListbook(response.data);
                }



                setLoading(false);

            } catch (err) {
                setLoading(false);
                console.log('Error when get list book =>', err)
            }
        }
        getListBook()
    }, [API_URL])

    if (loading) return <Loading />;

    return (
        <div className="block-page">

            <div className="books-page">
                <button type='button' className='addbook-submit' onClick={() => history.push("/books")}>
                    <FaArrowLeft size={22} />
                    <span className='fs-18 fw-6'>Trở lại</span>
                </button>
                {/* <Breadcrumb /> */}
                <div className="title">
                    <label htmlFor="language">Sách theo thể loại "{category}"</label><br />
                </div>
                <div className="books">


                    {listBook.map((item) => {
                        return (

                            <div className="book-card">
                                <img src={getUrlImage(item.image_url)} alt="" href={`/book/${item._id}`} ></img>
                                <Link to={`/book/${item._id}`}>

                                    <p className="bookcard-title"><b>{item.bookName}</b></p>

                                </Link>
                                <p className="bookcard-author"> {item.author}</p>
                                <p className="bookcard-author">{item.publisher}</p>

                                <div>
                                    <div className="bookcard-category">
                                        <p>{item.categories}</p>
                                    </div>
                                    <div className="bookcard-select">
                                        <Button onClick={showModal} >Mượn</Button>
                                    </div>
                                </div>
                                <div className="bookcard-emptybox"></div>
                            </div>
                        )
                    })}
                </div>
            </div>
            <Modal title="Basic Modal" open={isModalOpen} onOk={handleOk} onCancel={handleCancel}>
                <Title level={2}>Tạo phiếu mượn sách</Title>
                {/* Form for adding transaction */}
                <Form
                    // form={form}
                    onFinish={addTransaction}
                    layout="vertical"
                    style={{ margin: 33, }}
                >
                    <Row gutter={16}>
                        <Col span={12}>
                            <Form.Item
                                label="Độc giả"
                                name="borrowerId"
                                rules={[{ required: true, message: 'Vui lòng chọn độc giả' }]}
                                labelStyle={{ fontSize: '20px' }} // Thêm style cho nhãn
                            >
                                <Select
                                    placeholder="Chọn độc giả"
                                    onChange={handleSelectUser}
                                    style={{ fontSize: '16px' }} // Thêm style cho Select component
                                >
                                    {allMembers.map(member => (
                                        <Option key={member._id} value={member._id}>{member.userFullName}</Option>
                                    ))}
                                </Select>


                            </Form.Item>
                            <Form.Item label="Tên sách" name="bookIds" rules={[{ required: true, message: 'Vui lòng chọn sách' }]}>
                                <Select placeholder="Chọn sách" onChange={handleBookSelection}>
                                    {allBooks.map(book => (
                                        <Option key={book._id} value={book._id}>{book.bookName} : {book.bookCountAvailable}</Option>
                                    ))}
                                </Select>
                            </Form.Item>
                            <Form.Item label="Loại mượn" name="transactionType" rules={[{ required: true, message: 'Vui lòng chọn loại mượn' }]}>
                                <Select placeholder="Chọn loại mượn" onChange={setTransactionType}>
                                    {transactionTypes.map(type => (
                                        <Option key={type.value} value={type.value}>{type.text}</Option>
                                    ))}
                                </Select>
                            </Form.Item>
                        </Col>
                        <Col span={12}>

                            <Form.Item label="Từ ngày" name="fromDate" rules={[{ required: true, message: 'Vui lòng chọn từ ngày' }]}>
                                <DatePicker style={{ width: 640 }} placeholder="Chọn từ ngày" onChange={date => setFromDate(date)} />
                            </Form.Item>
                            <Form.Item label="Đến ngày" name="toDate" rules={[{ required: true, message: 'Vui lòng chọn đến ngày' }]}>
                                <DatePicker style={{ width: 640 }} placeholder="Chọn đến ngày" onChange={date => setToDate(date)} />
                            </Form.Item>
                        </Col>

                    </Row>

                    <Form.Item
                        style={{
                            paddingTop: 24,
                        }}
                    >
                        <Button type="primary" htmlType="submit" loading={isLoading}>Tạo</Button>
                    </Form.Item>
                </Form>

            </Modal>

            <Footer />
        </div>
    )
}

export default BookList
