import React, { useEffect, useState } from 'react'
import "../AdminDashboard.css"
import axios from "axios"
import { Dropdown } from 'semantic-ui-react'
import '../../Member/MemberDashboard.css'
import moment from "moment"
import {
    Paper,
    TableContainer,
    TablePagination
} from "@mui/material"
import { Select, DatePicker, Table, Typography, Button, Form, Row, Col, message } from 'antd';
const { Option } = Select;

function GetMember() {

    const API_URL = process.env.REACT_APP_API_URL

    const [allMembersOptions, setAllMembersOptions] = useState([])
    const [memberId, setMemberId] = useState(null)
    const [memberDetails, setMemberDetails] = useState(null)
    const [page, setPage] = useState(0);
    const [rowsPerPage, setRowsPerPage] = useState(10);
    const [allTransactions, setAllTransactions] = useState([]);
    useEffect(() => {
        const fetchData = async () => {
            try {
                let transactions;
                // if (borrowerId === null) {
                transactions = await TransController.getAllTrans();
                // } else {
                //   transactions = await TransController.getTransByUser(borrowerId);
                // }

                const activeTransactions = transactions.filter(transaction => transaction && transaction.transactionStatus && transaction.transactionStatus === "Active");
                setAllTransactions(activeTransactions);
            } catch (error) {
                console.error('Error fetching transactions:', error);
            }
        };
        fetchData();     // Gọi fetchData để lấy dữ liệu

    }, []);
    //Fetch Members
    useEffect(() => {
        const getMembers = async () => {
            try {
                const response = await axios.get(API_URL + "api/users/allmembers")
                setAllMembersOptions(response.data.map((member) => (
                    { value: `${member?._id}`, text: `${member?.userType === "Student" ? `${member?.userFullName}[${member?.admissionId}]` : `${member?.userFullName}[${member?.employeeId}]`}` }
                )))
            }
            catch (err) {
                console.log(err)
            }
        }
        getMembers()
    }, [API_URL])


    useEffect(() => {
        const getMemberDetails = async () => {
            if (memberId !== null) {
                try {
                    const response = await axios.get(API_URL + "api/users/getuser/" + memberId)
                    setMemberDetails(response.data)
                }
                catch (err) {
                    console.log("Error in fetching the member details")
                }
            }
        }
        getMemberDetails()
    }, [API_URL, memberId])


    return (
        <div>
            <div className='semanticdropdown getmember-dropdown'>
                <p className="dashboard-option-title">Chờ xử lý</p>

                {allTransactions.length > 0 ? (
                    <>
                        <TableContainer component={Paper} style={{ maxWidth: '1160px' }}>

                            <table className="admindashboard-table">
                                <thead>
                                    <tr>
                                        <th>Tên sách</th>
                                        <th>Người mượn</th>
                                        <th>Từ ngày</th>
                                        <th>Đến ngày</th>
                                        <th></th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {allTransactions.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((transaction, index) => (
                                        <tr key={index}>
                                            <td>{transaction.books || "N/A"}</td>
                                            <td>{transaction.userName || "N/A"}</td>
                                            <td>{transaction.fromDate || "N/A"}</td>
                                            <td>{transaction.toDate || "N/A"}</td>
                                            <td>
                                                <Button type="primary" onClick={() => handleUpdateTransaction(transaction._id)}>
                                                    Cho mượn
                                                </Button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </TableContainer>

                        <TablePagination
                            onRowsPerPageChange={(e) => {
                                setRowsPerPage(parseInt(e.target.value, 10))
                                setPage(0)
                            }}
                            component="div"
                            count={allTransactions.length}
                            rowsPerPage={rowsPerPage}
                            page={page}
                            onPageChange={(e, newPage) => setPage(newPage)}
                            style={{ maxWidth: '1160px' }}
                        />
                    </>
                ) : (
                    <table className="admindashboard-table">
                        <thead>

                            <tr>
                                <th>Tên sách</th>
                                <th>Người mượn</th>
                                <th>Từ ngày</th>
                                <th>Đến ngày</th>
                            </tr>

                        </thead>
                        <tbody>
                            <tr>
                                <td colSpan="5">Không có giao dịch nào được tìm thấy</td>
                            </tr>
                        </tbody>
                    </table>
                )}

                <Form.Item
                    label="Độc giả"
                    name="borrowerId"
                    rules={[{ required: true, message: 'Vui lòng chọn độc giả' }]}
                    labelStyle={{ fontSize: '20px' }} // Thêm style cho nhãn
                >
                    <Select
                        placeholder="Chọn độc giả"
                        onChange={setMemberId}
                        style={{ fontSize: '16px' }} // Thêm style cho Select component
                    >
                        {allMembersOptions.map(member => (
                            <Option key={member.value} value={member.value}>{member.text}</Option>
                        ))}
                    </Select>


                </Form.Item>
            </div>
            <div style={memberId === null ? { display: "none" } : {}}>
                <div className="member-profile-content" id="profile@member" style={memberId === null ? { display: "none" } : {}}>
                    <div className="user-details-topbar">
                        <img className="user-profileimage" src="./assets/images/Profile.png" alt=""></img>
                        <div className="user-info">
                            <p className="user-name">{memberDetails?.userFullName}</p>
                            <p className="user-id">{memberDetails?.userType === "Student" ? memberDetails?.admissionId : memberDetails?.employeeId}</p>
                            <p className="user-email">{memberDetails?.email}</p>
                            <p className="user-phone">{memberDetails?.mobileNumber}</p>
                        </div>
                    </div>
                    <div className="user-details-specific">
                        <div className="specific-left">
                            <div style={{ display: "flex", justifyContent: "space-between" }}>
                                <p style={{ display: "flex", flex: "0.5", flexDirection: "column" }}>
                                    <span style={{ fontSize: "18px" }}>
                                        <b>Tuổi</b>
                                    </span>
                                    <span style={{ fontSize: "16px" }}>
                                        {memberDetails?.age}
                                    </span>
                                </p>
                                <p style={{ display: "flex", flex: "0.5", flexDirection: "column" }}>
                                    <span style={{ fontSize: "18px" }}>
                                        <b>Giới tính</b>
                                    </span>
                                    <span style={{ fontSize: "16px" }}>
                                        {memberDetails?.gender}
                                    </span>
                                </p>
                            </div>
                            <div style={{ display: "flex", justifyContent: "space-between", marginTop: "30px" }}>
                                <p style={{ display: "flex", flex: "0.5", flexDirection: "column" }}>
                                    <span style={{ fontSize: "18px" }}>
                                        <b>Ngày sinh</b>
                                    </span>
                                    <span style={{ fontSize: "16px" }}>
                                        {memberDetails?.dob}
                                    </span>
                                </p>
                                <p style={{ display: "flex", flex: "0.5", flexDirection: "column" }}>
                                    <span style={{ fontSize: "18px" }}>
                                        <b>Địa chỉ</b>
                                    </span>
                                    <span style={{ fontSize: "16px" }}>
                                        {memberDetails?.address}
                                    </span>
                                </p>
                            </div>
                        </div>
                        <div className="specific-right">
                            <div style={{ display: "flex", flexDirection: "column", flex: "0.5" }}>
                                <p style={{ fontSize: "20px", display: "flex", alignItems: "center", justifyContent: "center" }}><b>Điểm tích lũy</b></p>
                                <p style={{ fontSize: "25px", fontWeight: "500", display: "flex", alignItems: "center", justifyContent: "center", marginTop: "15px" }}>540</p>
                            </div>
                            <div className="dashboard-title-line"></div>
                            <div style={{ display: "flex", flexDirection: "column", flex: "0.5" }}>
                                <p style={{ fontSize: "20px", display: "flex", alignItems: "center", justifyContent: "center" }}><b>Thứ hạng</b></p>
                                <p style={{ fontSize: "25px", fontWeight: "500", display: "flex", alignItems: "center", justifyContent: "center", marginTop: "15px" }}>{memberDetails?.points}</p>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="member-activebooks-content" id="activebooks@member">
                    <p style={{ fontWeight: "bold", fontSize: "22px", marginTop: "22px", marginBottom: "22px" }}>Đang mượn</p>
                    <table className="activebooks-table">
                        <tr>
                            <th>STT</th>
                            <th>Tên sách</th>
                            <th>Từ ngày</th>
                            <th>Đến ngày</th>
                            <th>Quá hạn</th>
                        </tr>
                        {
                            memberDetails?.activeTransactions?.filter((data) => {
                                return data.transactionType === "Issued"
                            }).map((data, index) => {
                                return (
                                    <tr key={index}>
                                        <td>{index + 1}</td>
                                        <td>{data.bookName}</td>
                                        <td>{data.fromDate}</td>
                                        <td>{data.toDate}</td>
                                        <td>{(Math.floor((Date.parse(moment(new Date()).format("MM/DD/YYYY")) - Date.parse(data.toDate)) / 86400000)) <= 0 ? 0 : (Math.floor((Date.parse(moment(new Date()).format("MM/DD/YYYY")) - Date.parse(data.toDate)) / 86400000)) * 10}</td>
                                    </tr>
                                )
                            })
                        }
                    </table>
                </div>

                <div className="member-reservedbooks-content" id="reservedbooks@member">
                    <p style={{ fontWeight: "bold", fontSize: "22px", marginTop: "22px", marginBottom: "22px" }}>Chờ xử lý</p>
                    <table className="activebooks-table">
                        <tr>
                            <th>STT</th>
                            <th>Tên sách</th>
                            <th>Từ ngày</th>
                            <th>Đến ngày</th>
                        </tr>
                        {
                            memberDetails?.activeTransactions?.filter((data) => {
                                return data.transactionType === "Reserved"
                            }).map((data, index) => {
                                return (
                                    <tr key={index}>
                                        <td>{index + 1}</td>
                                        <td>{data.bookName}</td>
                                        <td>{data.fromDate}</td>
                                        <td>{data.toDate}</td>
                                    </tr>
                                )
                            })
                        }
                    </table>
                </div>
                <div className="member-history-content" id="history@member">
                    <p style={{ fontWeight: "bold", fontSize: "22px", marginTop: "22px", marginBottom: "22px" }}>History</p>
                    <table className="activebooks-table">
                        <tr>
                            <th>STT</th>
                            <th>Tên sách</th>
                            <th>Từ ngày</th>
                            <th>Đến ngày</th>
                            <th>Ngày trả</th>
                        </tr>
                        {
                            memberDetails?.prevTransactions?.map((data, index) => {
                                return (
                                    <tr key={index}>
                                        <td>{index + 1}</td>
                                        <td>{data.bookName}</td>
                                        <td>{data.fromDate}</td>
                                        <td>{data.toDate}</td>
                                        <td>{data.returnDate}</td>
                                    </tr>
                                )
                            })
                        }
                    </table>
                </div>
            </div>
        </div>
    )
}

export default GetMember
