import React, { useContext, useEffect, useState } from 'react';
import { Select, DatePicker, Table, Typography, Button, Form, Row, Col, message } from 'antd';
import axios from 'axios';
import { AuthContext } from '../../../../Context/AuthContext';
import moment from 'moment';
import Loading from '../../../../Components/Loader/Loader';
// import "./adtransaction.css"

const { Option } = Select;
const { Title } = Typography;

function AddTransaction() {
  const API_URL = process.env.REACT_APP_API_URL;
  const { user } = useContext(AuthContext);
  const [isLoading, setIsLoading] = useState(false);
  const [borrowerId, setBorrowerId] = useState('');
  const [borrowerDetails, setBorrowerDetails] = useState({});
  const [bookIds, setBookIds] = useState([]);
  const [transactionType, setTransactionType] = useState('');
  const [fromDate, setFromDate] = useState(null);
  const [toDate, setToDate] = useState(null);
  const [allMembers, setAllMembers] = useState([]);
  const [allBooks, setAllBooks] = useState([]);
  const [recentTransactions, setRecentTransactions] = useState([]);

  const transactionTypes = [
    { value: 'Reserved', text: 'ƒê·∫∑t tr∆∞·ªõc' },
    { value: 'Issued', text: 'M∆∞·ª£n lu√¥n' },
  ];

  // Fetching all members
  useEffect(() => {
    const getMembers = async () => {
      try {
        const response = await axios.get(API_URL + 'api/users/allmembers');
        const allMembers = response.data.map(member => ({
          value: member._id,
          text: `${member.userFullName} [${member.userType === 'Student' ? member.admissionId : member.employeeId}]`,
        }));
        setAllMembers(allMembers);
      } catch (error) {
        console.log('Error fetching members:', error);
      }
    };
    getMembers();
  }, [API_URL]);

  // Fetching all books
  useEffect(() => {
    const getBooks = async () => {
      try {
        const response = await axios.get(API_URL + 'api/books/allbooks');
        const allBooks = response.data.map(book => ({
          value: book._id,
          text: book.bookName,
        }));
        setAllBooks(allBooks);
      } catch (error) {
        console.log('Error fetching books:', error);
      }
    };
    getBooks();
  }, [API_URL]);

  const handleBookSelection = selectedBooks => {
    setBookIds(selectedBooks);
  };

  const addTransaction = async values => {
    setIsLoading(true);
    try {
      // Your add transaction logic here
      message.success('T·∫°o phi·∫øu m∆∞·ª£n th√†nh c√¥ng üéâ');
    } catch (error) {
      console.log('Error adding transaction:', error);
      message.error('ƒê√£ x·∫£y ra l·ªói khi t·∫°o phi·∫øu m∆∞·ª£n.');
    } finally {
      setIsLoading(false);
    }
  };


  if (isLoading) return <Loading />;

  return (
    <div>
      <Title level={2}>T·∫°o phi·∫øu m∆∞·ª£n s√°ch</Title>
      {/* Form for adding transaction */}
      <Form
        onFinish={addTransaction}
        layout="vertical"
        style={{ margin: 20, }}
      >
        <Row gutter={16}>
          <Col span={7}>
            <Form.Item
              label="ƒê·ªôc gi·∫£"
              name="borrowerId"
              rules={[{ required: true, message: 'Vui l√≤ng ch·ªçn ƒë·ªôc gi·∫£' }]}
              labelStyle={{ fontSize: '20px' }} // Th√™m style cho nh√£n
            >
              <Select
                placeholder="Ch·ªçn ƒë·ªôc gi·∫£"
                onChange={setBorrowerId}
                style={{ fontSize: '16px' }} // Th√™m style cho Select component
              >
                {allMembers.map(member => (
                  <Option key={member.value} value={member.value}>{member.text}</Option>
                ))}
              </Select>
              <Form.Item label="T√™n s√°ch" name="bookIds" rules={[{ required: true, message: 'Vui l√≤ng ch·ªçn s√°ch' }]}>
                <Select mode="multiple" placeholder="Ch·ªçn s√°ch" onChange={handleBookSelection}>
                  {allBooks.map(book => (
                    <Option key={book.value} value={book.value}>{book.text}</Option>
                  ))}
                </Select>
              </Form.Item>
            </Form.Item>
            <Form.Item label="Lo·∫°i m∆∞·ª£n" name="transactionType" rules={[{ required: true, message: 'Vui l√≤ng ch·ªçn lo·∫°i m∆∞·ª£n' }]}>
              <Select placeholder="Ch·ªçn lo·∫°i m∆∞·ª£n" onChange={setTransactionType}>
                {transactionTypes.map(type => (
                  <Option key={type.value} value={type.value}>{type.text}</Option>
                ))}
              </Select>
            </Form.Item>
          </Col>
          <Col span={8}>

            <Form.Item label="T·ª´ ng√†y" name="fromDate" rules={[{ required: true, message: 'Vui l√≤ng ch·ªçn t·ª´ ng√†y' }]}>
              <DatePicker placeholder="Ch·ªçn t·ª´ ng√†y" onChange={date => setFromDate(date)} />
            </Form.Item>
            <Form.Item label="ƒê·∫øn ng√†y" name="toDate" rules={[{ required: true, message: 'Vui l√≤ng ch·ªçn ƒë·∫øn ng√†y' }]}>
              <DatePicker placeholder="Ch·ªçn ƒë·∫øn ng√†y" onChange={date => setToDate(date)} />
            </Form.Item>
          </Col>

        </Row>
        <Row gutter={16}>
          <Col span={12}>
            <Form.Item label="T·ª´ ng√†y" name="fromDate" rules={[{ required: true, message: 'Vui l√≤ng ch·ªçn t·ª´ ng√†y' }]}>
              <DatePicker placeholder="Ch·ªçn t·ª´ ng√†y" onChange={date => setFromDate(date)} />
            </Form.Item>
          </Col>
          <Col span={12}>
            <Form.Item label="ƒê·∫øn ng√†y" name="toDate" rules={[{ required: true, message: 'Vui l√≤ng ch·ªçn ƒë·∫øn ng√†y' }]}>
              <DatePicker placeholder="Ch·ªçn ƒë·∫øn ng√†y" onChange={date => setToDate(date)} />
            </Form.Item>
          </Col>
        </Row>
        <Form.Item
          style={{
            paddingTop: 24,
          }}
        >
          <Button type="primary" htmlType="submit" loading={isLoading}>Submit</Button>
        </Form.Item>
      </Form>

      {/* Table showing recent transactions */}
      <Title level={4}>Danh s√°ch phi·∫øu m∆∞·ª£n ch∆∞a x·ª≠ l√Ω</Title>
      <table className="admindashboard-table"
        // Adjust sidebar width here
        width={300}>
        <tr width={300}>
          <th>STT</th>
          <th>T√™n s√°ch</th>
          <th>Ng∆∞·ªùi m∆∞·ª£n</th>
          <th>Ng√†y m∆∞·ª£n</th>
        </tr>
        {recentTransactions.map((transaction, index) => {
          console.log("kiet => ", transaction);
          return (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{transaction.bookName}</td>
              <td>{transaction.borrowerId.userFullName}</td>
              <td>{transaction.updatedAt.slice(0, 10)}</td>
            </tr>
          )
        })}
      </table>
    </div>
  );
}

export default AddTransaction;
