import { React, useState, useRef, useEffect, useContext } from 'react'
import { Link } from 'react-router-dom'
import './Header.css';
import MenuIcon from '@material-ui/icons/Menu';
import ClearIcon from '@material-ui/icons/Clear';
import { FaSearch } from "react-icons/fa";
import { AuthContext } from "../../Context/AuthContext.js"
import { useHistory } from "react-router-dom";
import Navbar from "../Navbar/Navbar";
import {
    Avatar,
    Badge,
    Drawer,
    Button,
    List,
    Skeleton,
    Collapse,
    Typography

} from 'antd';
import { BellOutlined } from '@ant-design/icons';
const { Panel } = Collapse;
const { Text } = Typography;

const count = 10;
const fakeDataUrl = `https://randomuser.me/api/?results=${count}&inc=name,gender,email,nat,picture&noinfo`;
function Header() {
    const [menutoggle, setMenutoggle] = useState(false)
    const history = useHistory();
    const [initLoading, setInitLoading] = useState(true);
    const [loading, setLoading] = useState(false);
    const [data, setData] = useState([]);
    const [list, setList] = useState([]);
    const [open, setOpen] = useState(false);
    const [countNotify, setCountNotify] = useState(0);
    const [notifyStaff, setNotifyStaff] = useState(false);
    const [notifyUser, setNotifyUser] = useState(false);

    // lay Notify
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/notify/count'); // Thay đổi URL tùy thuộc vào đường dẫn của API
                if (!response.ok) {
                    throw new Error('Failed to fetch books');
                }
                const data = await response.json();
                setCountNotify(data); // Cập nhật state allBooks với dữ liệu từ API
            } catch (error) {
                console.error('Error fetching books:', error);
            }
        };

        fetchData(); // Gọi fetchData để lấy dữ liệu

    }, []);


    console.log("dem", countNotify)
    const onChange = (key) => {
        console.log(key);
    };
    const showDrawer = () => {
        setOpen(true);
    };
    const onClose = () => {
        setOpen(false);
    };
    const Toggle = () => {
        setMenutoggle(!menutoggle)
    }

    const closeMenu = () => {
        setMenutoggle(false)
    }

    const { user } = useContext(AuthContext);

    const [searchTerm, setSearchTerm] = useState("");


    const handleSearch = (event) => {
        event.preventDefault();
        // Gửi yêu cầu tìm kiếm
        console.log("Từ khóa tìm kiếm:", searchTerm);

        history.push(`/search/${searchTerm}`);

    };

    const handleKeyPress = (event) => {
        if (event.key === "Enter") {
            handleSearch(event);
        }
    };
    // thong bao 
    useEffect(() => {
        fetch(fakeDataUrl)
            .then((res) => res.json())
            .then((res) => {
                setInitLoading(false);
                setData(res.results);
                setList(res.results);
            });
    }, []);
    const onLoadMore = () => {
        setLoading(true);
        setList(
            data.concat(
                [...new Array(count)].map(() => ({
                    loading: true,
                    name: {},
                    picture: {},
                })),
            ),
        );
        fetch(fakeDataUrl)
            .then((res) => res.json())
            .then((res) => {
                const newData = data.concat(res.results);
                setData(newData);
                setList(newData);
                setLoading(false);
                // Resetting window's offsetTop so as to display react-virtualized demo underfloor.
                // In real scene, you can using public method of react-virtualized:
                // https://stackoverflow.com/questions/46700726/how-to-use-public-method-updateposition-of-react-virtualized
                window.dispatchEvent(new Event('resize'));
            });
    };
    const loadMore =
        !initLoading && !loading ? (
            <div
                style={{
                    textAlign: 'center',
                    marginTop: 12,
                    height: 32,
                    lineHeight: '32px',
                }}
            >
                <Button onClick={onLoadMore}>loading more</Button>
            </div>
        ) : null;

    return (
        <div className="header">
            <div className="logo-nav">
                <Link to="/">
                    <a href="#home">THƯ VIỆN MTA </a>
                </Link>
            </div>
            <div className="nav-right">

                <div className="search-container">
                    <input className="search-input" type="text" placeholder="Tìm kiếm..."
                        value={searchTerm}
                        onChange={(event) => setSearchTerm(event.target.value)}
                        onKeyUp={handleKeyPress}

                    />

                </div>


                <ul className={menutoggle ? "nav-options active" : "nav-options"}>
                    <li className="option" onClick={() => { closeMenu() }}>
                        <Link to="/">
                            <a href="#home">Trang chủ</a>
                        </Link>
                    </li>
                    <li className="option" onClick={() => { closeMenu() }}>
                        <Link to="/books">
                            <a href="#books">Kho sách</a>
                        </Link>
                    </li>

                    {!user ? (
                        <>
                            <li className="option" onClick={() => { closeMenu() }}>
                                <Link to="/signin">
                                    <a href="signin">Đăng nhập</a>
                                </Link>
                            </li>

                            <li className="option" onClick={() => { closeMenu() }}>
                                <Link to="/signup">
                                    <a href="signup">Đăng ký</a>
                                </Link>
                            </li>


                        </>
                    ) : (
                        <>
                            <li className="option" onClick={() => { closeMenu() }}>
                                <Link to="/signin">
                                    <a href="signin">Admin</a>
                                </Link>
                            </li>
                            <li className="option" onClick={() => { closeMenu() }}>
                                <a href="#">
                                    <Badge count={countNotify}>
                                        {/* <Avatar shape="square" size="large" /> */}
                                        <div className="bell-icon">
                                            <BellOutlined
                                                style={{
                                                    fontSize: '25px',
                                                    color: '#fff',
                                                    padding: '4px'
                                                }}
                                                onClick={showDrawer}
                                            />
                                        </div>
                                    </Badge>
                                </a>
                            </li>
                        </>
                    )}

                </ul>
            </div>
            <div className="mobile-menu" onClick={() => { Toggle() }}>
                {menutoggle ? (
                    <ClearIcon className="menu-icon" style={{ fontSize: 40 }} />
                ) : (
                    <MenuIcon className="menu-icon" style={{ fontSize: 40 }} />
                )}
            </div>
            <Drawer title="Thông báo " onClose={onClose} open={open}>
                <List
                    className="demo-loadmore-list"
                    loading={initLoading}
                    itemLayout="horizontal"
                    loadMore={loadMore}
                    dataSource={list}
                    renderItem={(item) => (

                        <List.Item
                        // actions={[<a key="list-loadmore-edit">edit</a>, <a key="list-loadmore-more">more</a>]}
                        >
                            {/* <Skeleton avatar title={false} loading={item.loading} active> */}
                            <List.Item.Meta
                                // avatar={<Text type="success" >[Get]</Text>}
                                title={<Collapse
                                    // accordion
                                    onChange={onChange}
                                    className="custom-collapse"
                                    bordered={false} // Bỏ viền cho Collapse
                                    style={{ backgroundColor: 'transparent' }}
                                >
                                    <Panel
                                        header={item.name?.last}
                                        key="1"
                                    // style={{
                                    //     fontSize: '18px', // Tăng kích thước của header
                                    //     fontWeight: 'bold' // Làm đậm cho header
                                    // }}
                                    >
                                        <p
                                            style={{
                                                fontSize: '16px', // Tăng kích thước của header
                                                fontWeight: 'normal' // Làm đậm cho header
                                            }}>
                                            con mèo con lallalalalallal hehehe </p>

                                    </Panel>
                                </Collapse>}
                            />

                            {/* </Skeleton> */}
                        </List.Item>

                    )}
                />
            </Drawer>

        </div >




    )
}

export default Header
